INSERT INTO `tin` (`idTin`, `lang`, `TieuDe`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`, `tags`, `created_at`, `updated_at`) VALUES
(1, 'vi', '\'Những ngày không quên\' kết thúc đẹp', 'Uyên (Phương Oanh) làm lành với Khoa (Đình Tú), bỏ ước mơ làm giàu ở thành phố trong phim \"Những ngày không quên\". \r\n\r\nTrong tập cuối phát sóng tối 16/6, Uyên, Khoa trở về làng Yên làm đám cưới. Trước đó, cả hai nuôi mộng làm giàu ở thành phố nhưng gặp nhiều biến cố, cám dỗ, dần xa cách. Em gái Uyên - Đào (Việt Hoa) - chia tay người tình già vì bị đánh ghen. Cô muốn làm thân lại với Cân - chàng trai trong làng từng bị cô lợi dụng tình cảm, nhưng bị anh từ chối.', 'upload/images/Uye-n-1606-1592369500548.jpg', '2020-06-17', 0, '<p>Song song với tuyến nh&acirc;n vật ở l&agrave;ng Y&ecirc;n, c&aacute;c nh&acirc;n vật sống ở th&agrave;nh phố cũng c&oacute; c&aacute;i kết vi&ecirc;n m&atilde;n. Huệ (Thu Quỳnh) c&oacute; thai. Trước đ&oacute;, c&ocirc; gặp&nbsp;<a href=\"https://vnexpress.net/hue-ve-nha-di-con-gap-song-gio-hon-nhan-4092392.html\">s&oacute;ng gi&oacute;</a>&nbsp;v&igrave; chồng ngoại t&igrave;nh, bị người thứ ba khi&ecirc;u kh&iacute;ch.&nbsp;Bảo (Quang Anh) chia tay bạn g&aacute;i mới v&igrave; dần nhận ra t&igrave;nh cảm thực sự d&agrave;nh cho Dương (Bảo H&acirc;n). Anh tấn c&ocirc;ng c&ocirc; quyết liệt, cả hai&nbsp;h&ocirc;n nhau trong tập cuối.&nbsp;</p>\r\n\r\n<p>Tr&ecirc;n fanpage phim, nhiều kh&aacute;n giả cho biết họ dự đo&aacute;n được kết phim v&agrave; h&agrave;i l&ograve;ng với số phận c&aacute;c nh&acirc;n vật ch&iacute;nh. Tuy nhi&ecirc;n, một số người tiếc nuối v&igrave; hai nh&acirc;n vật Khu&ecirc; (Hồng Diễm) v&agrave; Bảo (Hồng Đăng) của phim&nbsp;<em>Hoa hồng tr&ecirc;n ngực tr&aacute;i</em>&nbsp;xuất hiện chớp nho&aacute;ng trong c&aacute;c tập trước v&agrave; kh&ocirc;ng được nhắc đến ở tập cuối.&nbsp;</p>\r\n\r\n<p>Dương (Bảo H&acirc;n) v&agrave; Bảo (Quang Anh) trong tập cuối phim &quot;Những ng&agrave;y kh&ocirc;ng qu&ecirc;n&quot;. Video:&nbsp;<em>VFC.</em></p>\r\n\r\n<p><em><a href=\"https://vnexpress.net/nhung-ngay-khong-quen-tieng-cuoi-thoi-dich-4086344.html\">Những ng&agrave;y kh&ocirc;ng qu&ecirc;n</a></em>&nbsp;tận dụng tuyến nh&acirc;n vật từ c&aacute;c phim được y&ecirc;u th&iacute;ch trước đ&oacute; như&nbsp;<em>Về nh&agrave; đi con, C&ocirc; g&aacute;i nh&agrave; người ta.&nbsp;</em>Ph&aacute;t s&oacute;ng từ ng&agrave;y&nbsp;6/4, thời điểm dịch&nbsp;b&ugrave;ng ph&aacute;t, phim l&agrave; m&oacute;n ăn tinh thần của nhiều người trong l&uacute;c gi&atilde;n c&aacute;ch x&atilde; hội. T&aacute;c phẩm truyền tải th&ocirc;ng điệp &yacute; nghĩa về ph&ograve;ng chống dịch, &yacute; thức cộng đồng trong thời kỳ kh&oacute; khăn. Đạo diễn Đỗ Thanh Hải n&oacute;i&nbsp;&ecirc;k&iacute;p nhiều lần phải l&agrave;m việc ng&agrave;y đ&ecirc;m để kịp sản xuất phim. Tr&ecirc;n trường quay, họ chuẩn bị đầy đủ khẩu trang, nước rửa tay... để đảm bảo an to&agrave;n.&nbsp;</p>\r\n\r\n<p>Diễn vi&ecirc;n Phương Oanh n&oacute;i c&ocirc; bồi hồi khi phim kết th&uacute;c. Trong thời gian quay,&nbsp;c&ocirc; c&ugrave;ng l&uacute;c đ&oacute;ng một phim kh&aacute;c, cường độ l&agrave;m việc gấp ba lần ng&agrave;y thường. Diễn vi&ecirc;n sụt 5 kg v&igrave; &aacute;p lực. Phương Oanh&nbsp;th&iacute;ch nh&acirc;n vật&nbsp;Uy&ecirc;n trong phim v&igrave; vai diễn gi&uacute;p c&ocirc; tạo sự kh&aacute;c biệt sau th&agrave;nh c&ocirc;ng của&nbsp;<em>Quỳnh B&uacute;p B&ecirc;.&nbsp;</em></p>\r\n\r\n<ul>\r\n	<li><a href=\"https://vnexpress.net/nhung-ngay-khong-quen-tieng-cuoi-thoi-dich-4086344.html\">&#39;Những ng&agrave;y kh&ocirc;ng qu&ecirc;n&#39;: Tiếng cười thời dịch</a><a href=\"https://vnexpress.net/nhung-ngay-khong-quen-tieng-cuoi-thoi-dich-4086344.html#box_comment_vne\">&nbsp;19</a></li>\r\n	<li><a href=\"https://vnexpress.net/hue-ve-nha-di-con-gap-song-gio-hon-nhan-4092392.html\">Huệ &#39;Về nh&agrave; đi con&#39; gặp s&oacute;ng gi&oacute; h&ocirc;n nh&acirc;n</a></li>\r\n	<li><a href=\"https://vnexpress.net/nhung-ngay-khong-quen-ta-canh-cach-ly-hai-huoc-4089133.html\">&#39;Những ng&agrave;y kh&ocirc;ng qu&ecirc;n&#39; tả cảnh c&aacute;ch ly h&agrave;i hước</a></li>\r\n</ul>\r\n\r\n<p><strong>H&agrave; Thu</strong></p>', 6, 7, 2, 1, 1, 'phim', NULL, '2020-06-17 06:59:56'),
(2, 'vi', 'Người phụ nữ biến làng một cây thành 100.000 cây', 'TRUNG QUỐCNăm 1980, vợ chồng bà Bai chuyển đến vùng đất mới lập nghiệp, nơi có tên Nhất Khóa Thụ, có nghĩa là \"làng một cây\", ám chỉ sự khô cằn sỏi đá.\r\n\r\nNhất Khóa Thụ nằm ở khu tự trị Ninh Hạ, sở dĩ có cái tên này vì cả làng chỉ có đúng một cây xanh. Năm 27 tuổi, Bai Chunlan, ở vùng Ngô Trung, cùng chồng con kéo đồ đạc trên một tấm ván đến định cư ở làng Nhất Khóa Thụ, cách quê mình 8 km. Cùng thời điểm đó, cái cây duy nhất chết khô, chính thức biến nơi đây thành sa mạc.\r\n\r\n\"Không biết từ khi nào mà làng này tên là Nhất Khóa Thụ. Vài chục năm trước khi mới chuyển tới đây, không có màu xanh, chỉ có màu vàng vô tận của sa mạc\", Bai Chunlan, hiện 67 tuổi nói.', 'upload/images/159166768174370753-620-1000-8069-1592384192221.jpg', '2020-06-17', 0, '<p>Người đến, người đi, chỉ c&ograve;n khoảng chục gia đ&igrave;nh vẫn cố gắng b&aacute;m trụ, quyết t&acirc;m chống lại qu&aacute; tr&igrave;nh sa mạc h&oacute;a. Bai Chunlan mỗi ng&agrave;y đi&nbsp;16 km để trồng c&acirc;y.&nbsp;Chồng b&agrave;&nbsp;thức đ&ecirc;m để chăm s&oacute;c c&acirc;y. Họ đ&agrave;o s&acirc;u xuống c&aacute;t để lấy nước v&agrave; chỉ c&oacute; b&aacute;nh ng&ocirc; l&agrave;m thức ăn.</p>\r\n\r\n<p>&quot;Ch&uacute;ng t&ocirc;i đều l&agrave; những con người ngớ ngẩn. Ng&agrave;y đ&oacute; t&ocirc;i trồng c&acirc;y ban ng&agrave;y, ban đ&ecirc;m về chăm&nbsp;con. C&ograve;n chồng t&ocirc;i th&igrave; x&acirc;y tường đất chống gi&oacute; v&agrave; c&aacute;t để bảo vệ c&acirc;y con, ngủ lu&ocirc;n tr&ecirc;n sa mạc. Anh ấy thậm ch&iacute; c&ograve;n ngốc hơn t&ocirc;i&quot;, b&agrave; nhớ lại.</p>\r\n\r\n<p>Họ gieo g&igrave; tr&ecirc;n đất cũng kh&ocirc;ng ph&aacute;t triển. Nghe theo lời k&ecirc;u gọi của c&aacute;n bộ, chỉ c&oacute; trồng rừng mới giải quyết được nạn đ&oacute;i, vợ chồng Bai Chunlan vẫn ki&ecirc;n tr&igrave; năm n&agrave;y qua năm kh&aacute;c. Dần dần họ trồng được 100.000 c&acirc;y.</p>\r\n\r\n<p>Một lần b&agrave; m&atilde;i m&ecirc; trồng c&acirc;y m&agrave; kh&ocirc;ng hay biết con g&aacute;i m&igrave;nh bị gi&oacute; cuốn đi.&nbsp;Khi t&igrave;m thấy, con g&aacute;i b&agrave; bất tỉnh. May mắn em kh&ocirc;ng sao, nhưng đến giờ mỗi khi nhớ lại b&agrave; vẫn &aacute;y n&aacute;y với con.</p>\r\n\r\n<p>Năm&nbsp;1984, b&agrave;&nbsp;v&agrave; d&acirc;n l&agrave;ng trồng th&agrave;nh c&ocirc;ng 6.000 m2 l&uacute;a m&igrave;, chỉ thu được 4 bao tải. Nhưng như vậy họ đ&atilde; hạnh ph&uacute;c&nbsp;v&agrave; th&ecirc;m tin tưởng sẽ c&oacute; một ng&agrave;y đủ ăn.</p>\r\n\r\n<p><img alt=\"Bà Bai Chunlan. Ảnh: People.\" src=\"https://i1-giadinh.vnecdn.net/2020/06/17/photo-1-1592319551905104407784-3167-6967-1592384192.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2AodweasFsxG7U2_vLiELA\" /></p>\r\n\r\n<p>B&agrave; Bai Chunlan. Ảnh:&nbsp;<em>People.</em></p>\r\n\r\n<p>Chỉ học hết cấp một, nhưng sau 40 năm, b&agrave; Bai đ&atilde; trở th&agrave;nh một &quot;chuy&ecirc;n gia n&ocirc;ng nghiệp l&atilde;o luyện.&nbsp;V&agrave;o đầu xu&acirc;n năm 1984, Bai được ch&iacute;nh quyền tặng cho một số hạt giống nho. D&ugrave; chăm bẵm cẩn thận, c&acirc;y vẫn chết. Sau đ&oacute;, b&agrave;&nbsp;đi tham khảo &yacute; kiến của c&aacute;c chuy&ecirc;n gia, c&ugrave;ng họ nghi&ecirc;n cứu, sau n&agrave;y trồng lại, tỷ lệ trồng c&acirc;y sống được tr&ecirc;n 70%.</p>\r\n\r\n<p>Sau nhiều năm nghi&ecirc;n cứu, Bai Chunlan&nbsp;ph&aacute;t minh ra phương ph&aacute;p kiểm so&aacute;t 3 v&ograve;ng, trồng cỏ để giữ&nbsp;đất, liễu để trị sa mạc.&nbsp;Ở v&agrave;nh đai sa mạc th&igrave; đ&agrave;o giếng chứa nước.&nbsp;</p>\r\n\r\n<p>Đến năm 2005, b&agrave;&nbsp;đưa Nhất Kh&oacute;a Thụ&nbsp;v&agrave;o khai th&aacute;c du lịch. Ngo&agrave;i&nbsp;trồng trọt, chăn nu&ocirc;i, giờ c&oacute; th&ecirc;m h&igrave;nh thức du lịch mới lạ, v&ugrave;ng qu&ecirc; n&agrave;y nhanh ch&oacute;ng trở n&ecirc;n khấm kh&aacute;.</p>\r\n\r\n<p><img alt=\"Vùng đất không sự sống giờ thành khu du lịch mới. Ảnh: People.\" src=\"https://i1-giadinh.vnecdn.net/2020/06/17/photo-3-1592319551946114613505-8080-3307-1592384192.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=MP3et8jvYXb36iXF2JKvMA\" /></p>\r\n\r\n<p>V&ugrave;ng đất kh&ocirc;ng sự sống giờ th&agrave;nh khu du lịch mới. Ảnh:&nbsp;<em>People.</em></p>\r\n\r\n<p>Năm&nbsp;nay đ&atilde; gần 70 tuổi, b&agrave; Bai vẫn khỏe mạnh v&agrave; đi trồng c&acirc;y mỗi ng&agrave;y. &quot;Khi t&ocirc;i đến đ&acirc;y, c&aacute;i c&acirc;y huyền thoại của l&agrave;ng chết kh&ocirc;. Vợ chồng t&ocirc;i trồng một c&acirc;y nhỏ trước nh&agrave;. Bạn thấy đấy, giờ n&oacute; cao lớn đến mức hai người &ocirc;m mới xuể&quot;, b&agrave; Bai chia sẻ.&nbsp;</p>\r\n\r\n<p>Chồng của b&agrave; Bai mất năm 47 tuổi v&igrave; xơ gan v&agrave; kh&ocirc;ng thể nh&igrave;n thấy cảnh tượng trước mắt. Nhưng b&agrave; Bai vẫn tiếp tục ki&ecirc;n tr&igrave; với sứ mệnh phủ xanh v&ugrave;ng đất sa mạc. &quot;Trồng rừng ch&iacute;nh l&agrave; khởi nguồn hạnh ph&uacute;c trong t&ocirc;i. T&ocirc;i đ&atilde; t&igrave;m được c&ocirc;ng việc m&igrave;nh y&ecirc;u th&iacute;ch v&agrave; m&atilde;n nguyện với sự nghiệp của m&igrave;nh&quot;, b&agrave; n&oacute;i.</p>\r\n\r\n<p><strong>Bảo Nhi&ecirc;n&nbsp;</strong>(Theo&nbsp;<em>People</em>)</p>', 1, 1, 0, 1, 1, 'đời sống', NULL, NULL),
(3, 'vi', 'Kết hôn với con trai của bạn thân', 'TRUNG QUỐCTiểu Na cho biết, cô và bạn thân chuyển xưng hô từ \'chị - em\' sang \'mẹ - con\' nhưng họ không xấu hổ vì điều đó.\r\n\r\nMới đây mạng xã hội Trung Quốc nổ ra cuộc tranh cãi về chuyện có nên coi mối tình \"vợ 40, chồng 25 tuổi\" là chuyện bình thường hay không. Điều đặc biệt, chú rể là con trai bạn thân của người vợ.', 'upload/images/vo-chong-4806-1592296986784.jpg', '2020-06-22', 0, '<p>Theo đ&oacute;, Tiểu Na&nbsp;(t&ecirc;n c&ocirc; d&acirc;u)&nbsp;đ&atilde; gặp chồng hiện tại của m&igrave;nh năm cậu 8 tuổi. &quot;Đ&oacute; l&agrave;&nbsp;năm 2003, c&aacute;ch đ&acirc;y 17 năm. Thực sự t&ocirc;i đ&atilde; nh&igrave;n thấy chồng m&igrave;nh lớn l&ecirc;n h&agrave;ng ng&agrave;y&quot;, người phụ nữ n&agrave;y cho hay.</p>\r\n\r\n<p>Mặc d&ugrave; hai b&ecirc;n đ&atilde; quen biết nhau nhiều năm nhưng chỉ thực sự y&ecirc;u nhau 2 th&aacute;ng gần đ&acirc;y, khi họ c&oacute; cơ hội tiếp x&uacute;c v&agrave; tr&ograve; chuyện nhiều hơn. &quot;Đ&oacute; l&agrave; một người phụ nữ trưởng th&agrave;nh v&agrave; đ&aacute;ng tin cậy&quot;, Tiểu Na tiết lộ đ&aacute;nh gi&aacute; của chồng về m&igrave;nh tr&ecirc;n trang c&aacute; nh&acirc;n.</p>\r\n\r\n<p><img alt=\"Tiểu Na đăng ảnh cô và chồng lần đầu gặp nhau 17 năm trước và hiện tại. Ảnh: misbbs.com\" src=\"https://i1-giadinh.vnecdn.net/2020/06/16/vo-chong-4806-1592296986.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=98S_MUGahrdUFiuOga9Dzw\" /></p>\r\n\r\n<p>Tiểu Na đăng ảnh c&ocirc; v&agrave; chồng lần đầu gặp nhau 17 năm trước v&agrave; hiện tại. Ảnh:&nbsp;<em>misbbs.com</em></p>\r\n\r\n<p>Tiểu Na đ&atilde; từng c&oacute; một đời chồng v&agrave; 3 đứa con ri&ecirc;ng nhưng điều đ&oacute; kh&ocirc;ng ảnh hưởng đến mối quan hệ hiện tại của họ. Thậm ch&iacute; c&ocirc; c&ograve;n đăng ảnh vợ chồng mới cưới l&ecirc;n Weibo, nhận được lời khen ngợi v&igrave; ngoại h&igrave;nh của cả hai kh&aacute; tương xứng.</p>\r\n\r\n<p>Thậm ch&iacute; người phụ nữ 40&nbsp;tuổi n&agrave;y c&ograve;n đăng bức h&igrave;nh lần đầu ti&ecirc;n chụp với người chồng hiện tại &quot;Đ&oacute; l&agrave; ng&agrave;y m&agrave; bạn th&acirc;n t&ocirc;i nhờ tr&ocirc;ng chồng t&ocirc;i b&acirc;y giờ - khi đ&oacute; mới l&agrave; cậu b&eacute; 8 tuổi. T&ocirc;i đ&atilde; đưa cậu đi ăn rồi đến trung t&acirc;m mua sắm chơi. H&ocirc;m đ&oacute; kh&aacute; vui vẻ&quot;, Tiểu Na nhớ lại.</p>\r\n\r\n<p>Người phụ nữ n&agrave;y cho biết, mẹ chồng kh&ocirc;ng hề phản đối mối quan hệ của hai người, d&ugrave; b&agrave; cũng kh&ocirc;ng ủng hộ. V&agrave;o ng&agrave;y cưới, b&agrave; cũng chỉ n&oacute;i với c&ocirc; một c&acirc;u &quot;Nếu cả hai hạnh ph&uacute;c th&igrave; h&atilde;y cứ đến với nhau&quot;.</p>\r\n\r\n<p>C&acirc;u chuyện của Tiểu Na ngay lập tức g&acirc;y ra một cuộc thảo luận s&ocirc;i nổi tr&ecirc;n Weibo. Nhiều người cho biết, sau khi đọc được tin n&agrave;y, họ cảm thấy &quot;thực sự lo lắng cho con trai m&igrave;nh&quot;. Tuy nhi&ecirc;n cũng c&oacute; &yacute; kiến cho rằng, trong t&igrave;nh y&ecirc;u, tuổi t&aacute;c chưa bao giờ l&agrave; vấn đề ch&iacute;nh.</p>\r\n\r\n<ul>\r\n	<li><a href=\"https://vnexpress.net/tai-hon-vo-cu-sau-31-nam-ly-di-4095442.html\">T&aacute;i h&ocirc;n vợ cũ sau 31 năm ly dị</a></li>\r\n</ul>\r\n\r\n<p><strong>Vy Tran</strong>g (Theo&nbsp;<em>misbbs.com</em>)</p>', 1, 1, 0, 1, 1, 'đời sống', NULL, NULL),
(4, 'vi', 'Khách đến Lào Cai hưởng ưu đãi đến 60%', 'Các đơn vị du lịch cam kết giảm giá 10 - 60% nhưng du khách vẫn được sử dụng nguyên chất lượng dịch vụ. \r\n\r\nUBND tỉnh Lào Cai phối hợp với Hiệp hội Du lịch Việt Nam ngày 16/6 tổ chức Hội nghị kích cầu du lịch Tây Bắc – Lào Cai và tọa đàm xây dựng sản phẩm du lịch đặc sắc. Chương trình có sự tham gia của đại diện lãnh đạo tỉnh Lào Cai và hơn 150 đơn vị kinh doanh dịch vụ du lịch, lữ hành trên cả nước', 'upload/images/Khach-d-n-Lao-Cai-1-9890-1592379439871.jpg', '2020-06-17', 0, '<p>Để khắc phục hậu quả của Covid-19, tỉnh L&agrave;o Cai đ&atilde; triển khai g&oacute;i k&iacute;ch cầu du lịch lần thứ nhất. Từ th&aacute;ng 5/2020,&nbsp;tỉnh mỗi tuần trung b&igrave;nh đ&oacute;n khoảng 40.000 lượt kh&aacute;ch, tuần cao điểm l&ecirc;n tới 98.000 lượt kh&aacute;ch, &ocirc;ng H&agrave; Văn Thắng, Gi&aacute;m đốc Sở VHTTDL L&agrave;o Cai cho biết.</p>\r\n\r\n<p><img alt=\"Toàn cảnh Fansipan Legend nhìn từ trên cao. Ảnh: Huỳnh Phương.\" src=\"https://i1-dulich.vnecdn.net/2020/06/17/Khach-d-n-Lao-Cai-1-9890-1592379439.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=B_4NU6e2KDBEYjAUxyEIxQ\" /></p>\r\n\r\n<p>To&agrave;n cảnh Fansipan Legend nh&igrave;n từ tr&ecirc;n cao. Ảnh:&nbsp;<em>Trần Minh Dũng.</em></p>\r\n\r\n<p>Với triển vọng t&iacute;ch cực n&agrave;y, L&agrave;o Cai tiếp tục tổ chức chương tr&igrave;nh k&iacute;ch cầu du lịch h&egrave; lần thứ hai, với 90 đơn vị đăng k&yacute; tham gia. Theo chương tr&igrave;nh, c&oacute; 4 khu, điểm tham quan &aacute;p dụng mức giảm từ 10 - 60%; 48 cơ sở lưu tr&uacute;, kh&aacute;ch sạn c&oacute; mức giảm từ 10 - 55%; 12 nh&agrave; h&agrave;ng giảm gi&aacute; từ 10 - 20%; 4 đơn vị vận chuyển kh&aacute;ch du lịch v&agrave; 4 c&ocirc;ng ty lữ h&agrave;nh đăng k&yacute; mức giảm từ 20 - 30%.</p>\r\n\r\n<p>Ngo&agrave;i ra, L&agrave;o Cai c&ograve;n dự kiến tổ chức một số hoạt động du lịch hấp dẫn trong năm 2020 như Lễ hội hoa hồng v&agrave; rượu vang Sa Pa lần đầu ti&ecirc;n trong Lễ hội m&ugrave;a h&egrave; Sa Pa, Lễ hội m&ugrave;a thu huyện B&aacute;t X&aacute;t năm 2020, Giải Marathon vượt n&uacute;i quốc tế VMM, Giải Marathon Fansipan...&nbsp;Du kh&aacute;ch c&ograve;n c&oacute; cơ hội trải nghiệm sản phẩm mới Du lịch trượt c&aacute;p mạo hiểm&nbsp;tại Vườn Quốc gia Ho&agrave;ng Li&ecirc;n.</p>\r\n\r\n<p>Theo &ocirc;ng Vũ Thế B&igrave;nh - Ph&oacute; Chủ tịch Thường trực Hiệp hội Du lịch Việt Nam, sau Covid-19, c&aacute;c tỉnh T&acirc;y Bắc cần tập trung đẩy nhanh chương tr&igrave;nh k&iacute;ch cầu du lịch, tạo kh&ocirc;ng kh&iacute; du lịch s&ocirc;i động, x&acirc;y dựng h&igrave;nh ảnh một điểm đến an to&agrave;n để h&uacute;t kh&aacute;ch. &Ocirc;ng cũng lưu &yacute; c&aacute;c địa phương k&iacute;ch cầu kh&ocirc;ng chỉ khuyến m&atilde;i, giảm gi&aacute; đơn thuần m&agrave; phải ch&uacute; trọng v&agrave;o chất lượng dịch vụ, bởi đ&acirc;y l&agrave; yếu tố quyết định.</p>\r\n\r\n<p><img alt=\"Toàn cảnh Hội nghị kích cầu du lịch Tây Bắc - Lào Cai. Ảnh: Ngân Dương. \" src=\"https://i1-dulich.vnecdn.net/2020/06/17/Khach-d-n-Lao-Cai-2-4657-1592379439.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7lyulEQBNOHkhQliW19iQQ\" /></p>\r\n\r\n<p>Hội nghị k&iacute;ch cầu du lịch T&acirc;y Bắc - L&agrave;o Cai t&igrave;m giải ph&aacute;p để thu h&uacute;t du kh&aacute;ch quay lại. Ảnh:&nbsp;<em>Ng&acirc;n Dương.&nbsp;</em></p>\r\n\r\n<p>&Ocirc;ng Nguyễn Hữu T&acirc;n - đại diện C&ocirc;ng ty du lịch Vietravel (TP.HCM), cho rằng c&aacute;c tỉnh T&acirc;y Bắc hiện đang bị tr&ugrave;ng lặp c&aacute;c sản phẩm du lịch sinh th&aacute;i. V&igrave; vậy, ở mỗi địa phương n&ecirc;n chọn ra 2 - 3 sản phẩm du lịch đặc trưng, kh&aacute;c biệt để l&agrave;m điểm nhấn thu h&uacute;t du kh&aacute;ch.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, hạ tầng giao th&ocirc;ng phục vụ du lịch cũng l&agrave; vấn đề được quan t&acirc;m. Theo &ocirc;ng Nguyễn Phi Hồng Nguy&ecirc;n, đại diện c&ocirc;ng ty du lịch Vietpromotion (Nha Trang), khoảng c&aacute;ch giữa c&aacute;c điểm du lịch T&acirc;y Bắc qu&aacute; xa, đường đi kh&oacute; khăn n&ecirc;n kh&oacute; đảm bảo sức khỏe cho du kh&aacute;ch. Tại c&aacute;c địa phương cũng chưa c&oacute; c&ocirc;ng ty lữ h&agrave;nh chuy&ecirc;n nghiệp để kết nối&nbsp;triển khai chương tr&igrave;nh tour d&agrave;i ng&agrave;y.</p>\r\n\r\n<p>Đồng t&igrave;nh với quan điểm n&agrave;y, b&agrave; Nguyễn Thị Kim Li&ecirc;n, đại diện&nbsp;c&ocirc;ng ty OTG Travel (Đ&agrave; Nẵng),cho rằng&nbsp;T&acirc;y Bắc rất mới mẻ v&agrave; tiềm năng với du kh&aacute;ch miền Trung. Đến được T&acirc;y Bắc, du kh&aacute;ch miền Trung phải trải qua h&agrave;nh tr&igrave;nh d&agrave;i ng&agrave;y v&agrave; sử dụng nhiều phương tiện di chuyển. Để hấp dẫn du kh&aacute;ch,&nbsp;c&aacute;c doanh nghiệp du lịch miền Trung&nbsp;cần hợp t&aacute;c với nhau để triển khai nhiều tour T&acirc;y Bắc hơn, x&acirc;y dựng c&aacute;c g&oacute;i sản phẩm mới&nbsp;để k&iacute;ch cầu.</p>\r\n\r\n<p><strong>Ng&acirc;n Dương</strong></p>', 3, 3, 0, 1, 1, 'du lịch', NULL, NULL),
(5, 'vi', 'Đi quá giới hạn với cô chủ nhà gấp đôi tuổi', 'Cháu 19 tuổi, học đại học ngành kỹ thuật ở Hà Nội, độc thân. Ngoài giờ học chủ yếu cháu ở nhà chơi game, tập gym và dạy gia sư.', 'upload/images/Anh-Kiet443.jpg', '2020-07-17', 0, '<p>Ch&aacute;u 19&nbsp;tuổi,&nbsp;học đại học ng&agrave;nh kỹ thuật ở H&agrave; Nội,&nbsp;độc th&acirc;n. Ngo&agrave;i giờ&nbsp;học&nbsp;chủ yếu ch&aacute;u&nbsp;ở nh&agrave; chơi game, tập gym v&agrave; dạy gia sư.&nbsp;</p>\r\n\r\n<p>Mọi chuyện&nbsp;kh&ocirc;ng c&oacute; vấn đề g&igrave; với ch&aacute;u&nbsp;cho đến&nbsp;c&aacute;ch đ&acirc;y 4 th&aacute;ng, c&ocirc; chủ nh&agrave; trọ li&ecirc;n tục&nbsp;sang cho đồ ăn v&agrave; nước uống, rồi c&ograve;n nhờ ch&aacute;u&nbsp;l&agrave;m một v&agrave;i việc sửa chữa đồ trong gia đ&igrave;nh.</p>\r\n\r\n<p>C&ocirc; 37 tuổi, c&oacute; một con g&aacute;i lớn&nbsp;đang học cấp 3 nhưng em ấy&nbsp;sống c&ugrave;ng &ocirc;ng b&agrave; ngoại gần trường học, ở quận kh&aacute;c. Một&nbsp;tối, c&ocirc; nhờ ch&aacute;u&nbsp;xuống sửa gi&uacute;p cửa nh&agrave; bị nước tr&agrave;n v&agrave;o do trời mưa. Ch&aacute;u&nbsp;sửa xong c&ocirc; k&ecirc;u ngủ lại ở ph&ograve;ng kế b&ecirc;n, đề ph&ograve;ng&nbsp;đ&ecirc;m mưa to hơn nước tr&agrave;n v&agrave;o nh&agrave;. Ch&aacute;u&nbsp;uống nước v&agrave; ăn hoa quả&nbsp;c&ocirc; đưa rồi&nbsp;đi ngủ, nửa đ&ecirc;m thấy&nbsp;người n&oacute;ng rực. Rồi c&ocirc;&nbsp;v&agrave;o ph&ograve;ng v&agrave; &ocirc;m ch&aacute;u, kh&ocirc;ng biết tại sao l&uacute;c đ&oacute; ch&aacute;u&nbsp;lại đồng &yacute; gần gũi c&ocirc;. Tiếp theo l&agrave; một thời gian d&agrave;i chuyện đ&oacute; lặp đi lặp lại, c&oacute; những h&ocirc;m 2-3 lần.</p>\r\n\r\n<p>Ch&aacute;u&nbsp;cảm thấy rất mệt mỏi v&agrave; t&ugrave;&nbsp;t&uacute;ng. Xin n&oacute;i th&ecirc;m l&agrave; vợ&nbsp;chồng c&ocirc; đ&atilde; ly th&acirc;n, ch&uacute; kia đi ở với c&ocirc; kh&aacute;c. Ch&aacute;u muốn chấm dứt mối quan hệ n&agrave;y th&igrave; c&ocirc; đe dọa sẽ gửi những clip trong l&uacute;c gần gũi ch&aacute;u đến nh&agrave; trường, bạn b&egrave;,&nbsp;gia đ&igrave;nh ch&aacute;u. Ch&aacute;u&nbsp;kh&ocirc;ng biết phải l&agrave;m như thế n&agrave;o, mong được mọi người gi&uacute;p ch&aacute;u.</p>\r\n\r\n<p><strong>Khải</strong></p>', 4, 3, 0, 1, 1, 'tâm sự', NULL, NULL),
(6, 'vi', 'Quán quân \'Hãy nghe tôi hát\' 2019 ra album đầu tay', 'TP HCMTuyết Mai ra mắt CD đầu tay \"Mùa hè đẹp nhất\" với tám ca khúc, sau hai tuần đoạt quán quân cuộc thi hát. \r\n\r\nTối 16/6, tại tiệc mừng giải thưởng kết hợp ra sản phẩm âm nhạc, Tuyết Mai cho biết may mắn khi được chồng người Malaysia ủng hộ ca hát, giúp ra album. Chồng cô - Jang Sarban - nói tự hào về vợ.', 'upload/images/tuyet-mai-3643-1592376993596.jpg', '2020-07-16', 0, '<p>Ca sĩ chọn bốn ca kh&uacute;c ghi dấu ấn trong&nbsp;<em>H&atilde;y nghe t&ocirc;i h&aacute;t&nbsp;</em>như:&nbsp;<em>Người em sầu mộng&nbsp;</em>(Y V&acirc;n - thơ Lưu Trọng Lư),&nbsp;<em>Nếu một mai em sẽ qua đời&nbsp;</em>(Phạm Duy),&nbsp;<em>Ph&ocirc;i pha&nbsp;</em>(Trịnh C&ocirc;ng Sơn),<em>&nbsp;Chiều về tr&ecirc;n s&ocirc;ng&nbsp;</em>(Phạm Duy)&nbsp;đưa v&agrave;o CD. Bốn b&agrave;i c&ograve;n lại:&nbsp;<em>T&igrave;nh kh&uacute;c m&ugrave;a xu&acirc;n&nbsp;</em>(Ng&ocirc; Thụy Mi&ecirc;n),<em>&nbsp;Gửi gi&oacute; cho m&acirc;y ng&agrave;n bay&nbsp;</em>(Đo&agrave;n Chuẩn, Từ Linh),&nbsp;<em>M&ugrave;a h&egrave; đẹp nhất&nbsp;</em>(Đức Huy)<em>&nbsp;</em>v&agrave;<em>&nbsp;Nửa hồn đau thương&nbsp;</em>(Phạm Đ&igrave;nh Chương) do Tuyết Mai v&agrave; chồng c&ugrave;ng chọn để ph&ugrave; hợp với d&ograve;ng nhạc xưa c&ocirc; đang theo đuổi. Album được thu live với sự hỗ trợ của ban nhạc gồm guitar: Nhật Đ&ocirc;ng, saxophone: Ho&agrave;ng Thi, trống: Huỳnh Ph&uacute;c, keyboard: Ho&agrave;ng Thiện, double bass: Ho&agrave;ng Nam.&nbsp;</p>\r\n\r\n<p>Tuyết Mai sinh năm 1988, trong gia đ&igrave;nh l&agrave;m nh&agrave; n&ocirc;ng ở L&acirc;m Đồng. C&ocirc; học thanh nhạc tại&nbsp;Nhạc viện TP HCM. Sau khi thi&nbsp;<em>X -Factor 2014,&nbsp;</em>c&ocirc;&nbsp;kết h&ocirc;n v&agrave; theo chồng sang Malaysia định cư. Năm 2018, ca sĩ về nước,&nbsp;trở lại &acirc;m nhạc với cuộc thi&nbsp;<em>Tuyệt đỉnh song ca.&nbsp;</em>C&ocirc; c&oacute; con g&aacute;i (hơn ba tuổi).&nbsp;</p>', 2, 6, 0, 1, 1, 'âm nhạc', NULL, NULL),
(7, 'vi', 'Xoáy nước giữa đại dương nhìn từ vũ trụ', 'Phi hành gia trên Trạm Vũ trụ Quốc tế (ISS) ghi lại hiện tượng độc đáo khi bay qua Nam Thái Bình Dương.', 'upload/images/Xoay-nuoc-trong-bai-1515-1592363782709.jpg', '2020-06-25', 0, '<p>Phi h&agrave;nh gia Mỹ Doug Hurley h&ocirc;m 16/6 chia sẻ ảnh chụp Tr&aacute;i Đất l&ecirc;n mạng x&atilde; hội Twitter. &quot;Xo&aacute;y nước m&agrave;u xanh nhạt n&agrave;y thu h&uacute;t sự ch&uacute; &yacute; của t&ocirc;i khi ch&uacute;ng t&ocirc;i bay qua Nam Th&aacute;i B&igrave;nh Dương&quot;, Hurley viết.</p>\r\n\r\n<p>Đại dương l&agrave; khối nước lớn li&ecirc;n tục chuyển động, theo Cơ quan Đại dương Quốc gia Mỹ (NOS). Những d&ograve;ng chảy ổn định v&agrave; li&ecirc;n tục được gọi l&agrave; hải lưu. Đ&ocirc;i khi ch&uacute;ng c&oacute; thể bị ngắt đoạn v&agrave; tạo ra những d&ograve;ng chảy tr&ograve;n gọi l&agrave; xo&aacute;y nước. Xo&aacute;y nước dạng n&agrave;y khiến c&aacute;c chất dinh dưỡng vốn ở v&ugrave;ng nước s&acirc;u v&agrave; lạnh gi&aacute; hơn nổi l&ecirc;n gần mặt biển. Đ&acirc;y nhiều khả năng l&agrave; hiện tượng xuất hiện trong bức ảnh của Hurley.</p>\r\n\r\n<p>Trạm ISS hoạt động ở độ cao khoảng 400 km, bay 16 v&ograve;ng quanh Tr&aacute;i Đất mỗi ng&agrave;y. Hurley c&ugrave;ng phi h&agrave;nh gia Bob Behnken bay l&ecirc;n trạm nhờ t&agrave;u Crew Dragon v&agrave; t&ecirc;n lửa Falcon 9 của SpaceX c&aacute;ch đ&acirc;y hơn hai tuần. Chuyến bay mang t&ecirc;n&nbsp;<a href=\"https://vnexpress.net/spacex-phong-thanh-cong-tau-vu-tru-cho-nguoi-len-tram-iss-4107955.html\">Demo-2</a>, đ&aacute;nh dấu lần đầu ti&ecirc;n NASA ph&oacute;ng t&agrave;u chở người từ đất Mỹ sau 9 năm.</p>\r\n\r\n<p>Demo-2 l&agrave; bước đi lớn cuối c&ugrave;ng để t&agrave;u Crew Dragon được chứng nhận theo chương tr&igrave;nh Commercial Crew của NASA v&agrave; bắt đầu c&aacute;c nhiệm vụ vận chuyển tới trạm ISS.</p>\r\n\r\n<p>&quot;Việc t&agrave;u Crew Dragon được chứng nhận v&agrave; vận h&agrave;nh thường xuy&ecirc;n sẽ cho ph&eacute;p NASA tiếp tục đầu tư v&agrave;o&nbsp;những c&ocirc;ng nghệ v&agrave; nghi&ecirc;n cứu quan trọng tr&ecirc;n trạm ISS, mang lại lợi &iacute;ch cho con người tr&ecirc;n Tr&aacute;i Đất cũng như đặt nền tảng cho sứ mệnh&nbsp;kh&aacute;m ph&aacute; Mặt Trăng v&agrave; sao Hỏa trong tương lai, bắt đầu từ chương tr&igrave;nh Artemis&quot;, NASA cho biết. Artemis l&agrave; chương tr&igrave;nh được x&acirc;y dựng với mục đ&iacute;ch đưa con người quay lại Mặt Trăng v&agrave; đưa người phụ nữ đầu ti&ecirc;n đặt ch&acirc;n l&ecirc;n nơi&nbsp;n&agrave;y v&agrave;o năm 2024.</p>\r\n\r\n<p><strong>Thu Thảo</strong>&nbsp;(Theo&nbsp;<em>Mirror</em>)</p>', 1, 1, 0, 1, 1, 'đời sống', NULL, NULL),
(8, 'vi', 'Phúc \'XO\' lĩnh 12 năm tù', 'Sau 5 phút hỏi về số tiền 600 triệu đồng Trần Ngọc Phúc, tức Phúc \"XO\", bị cáo buộc thu lợi bất chính, tòa tuyên bị cáo 12 năm tù và phạt bổ sung 50 triệu.', 'upload/images/phuc-xo-11-9253-1592381507103.jpg', '2020-06-29', 0, '<p>Mở đầu phi&ecirc;n l&agrave;m việc chiều 17/6, chủ tọa Ng&ocirc; Ngọc Thắng cho biết cần l&agrave;m r&otilde; th&ecirc;m&nbsp;một số th&ocirc;ng tin li&ecirc;n quan vụ &aacute;n n&ecirc;n quay lại phần x&eacute;t hỏi.</p>\r\n\r\n<p>Trần Ngọc Ph&uacute;c, 37 tuổi, v&agrave; 7&nbsp;đồng phạm bị TAND TP HCM x&eacute;t xử về tội&nbsp;<em>Chứa chấp sử dụng tr&aacute;i ph&eacute;p chất ma tu&yacute;&nbsp;</em>theo khoản 2 Điều 256 BLHS năm 2015, khung h&igrave;nh phạt l&agrave;&nbsp;7-15 năm t&ugrave;.&nbsp;</p>\r\n\r\n<p>Thẩm ph&aacute;n hỏi b&agrave; Diễm - vợ Ph&uacute;c, về&nbsp;600 triệu đồng thu lợi bất ch&iacute;nh. B&agrave; n&agrave;y cho biết&nbsp;đ&atilde; nộp cho cơ quan điều tra to&agrave;n bộ số tiền, đồng &yacute; sung c&ocirc;ng&nbsp;quỹ&nbsp;nếu t&ograve;a cho rằng c&oacute; căn cứ x&aacute;c định đ&acirc;y&nbsp;l&agrave; tiền thu lợi bất ch&iacute;nh.</p>\r\n\r\n<p>Được hỏi &yacute; kiến,&nbsp;Ph&uacute;c n&oacute;i: &quot;Nếu được, xin t&ograve;a trả lại&nbsp;600 triệu vợ bị c&aacute;o&nbsp;đ&atilde; nộp v&agrave;&nbsp;40 triệu cơ quan điều tra thu giữ khi kh&aacute;m x&eacute;t, để cha bị c&aacute;o d&ugrave;ng số tiền n&agrave;y ủng hộ Bộ Y tế&nbsp;ph&ograve;ng chống dịch Covid-19&quot;.</p>\r\n\r\n<p>Sau 5 ph&uacute;t nghị &aacute;n,&nbsp;HĐXX quay lại&nbsp;tuy&ecirc;n &aacute;n.</p>\r\n\r\n<p>Ngồi lọt thỏm giữa hai h&agrave;ng bị c&aacute;o đứng nghe tuy&ecirc;n &aacute;n, Ph&uacute;c m&iacute;m m&ocirc;i, hai tay đan chặt, vẻ căng thẳng.</p>\r\n\r\n<p><img alt=\"Các đồng phạm của Phúc XO nghe tuyên án. Ảnh: Quỳnh Trần.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/17/phuc-xo-12-1112-1592381507.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=FT38dj3hAy5BSZC-nLB74A\" /></p>\r\n\r\n<p>C&aacute;c đồng phạm của Ph&uacute;c &quot;XO&quot; nghe tuy&ecirc;n &aacute;n.&nbsp;Ảnh:&nbsp;<em>Quỳnh Trần.</em></p>\r\n\r\n<p>HĐXX cho rằng, c&aacute;c chứng cứ trong hồ sơ vụ &aacute;n v&agrave; lời khai của c&aacute;c bị c&aacute;o đ&atilde; đủ cơ sở x&aacute;c định Ph&uacute;c &quot;XO&quot; v&agrave; c&aacute;c nh&acirc;n vi&ecirc;n của m&igrave;nh thực hiện h&agrave;nh vi phạm tội như c&aacute;o trạng truy tố.</p>\r\n\r\n<p>T&ograve;a đ&aacute;nh gi&aacute;&nbsp;h&agrave;nh vi của c&aacute;c bị c&aacute;o l&agrave; nguy hiểm cho x&atilde; hội. Anh em Ph&uacute;c v&agrave; đồng phạm biết ma t&uacute;y g&acirc;y nhiều hệ lụy cho x&atilde; hội nhưng vẫn chứa chấp,&nbsp;t&agrave;ng trữ để sử dụng n&ecirc;n&nbsp;cần xử l&yacute; nghi&ecirc;m.</p>\r\n\r\n<p>Video Player is loading.</p>\r\n\r\n<p>Dừng</p>\r\n\r\n<p>Hiện tại&nbsp;0:05</p>\r\n\r\n<p>/</p>\r\n\r\n<p>Thời lượng&nbsp;1:24</p>\r\n\r\n<p>Đ&atilde; tải: 0%</p>\r\n\r\n<p>Tiến tr&igrave;nh: 0%</p>\r\n\r\n<p>Bỏ tắt tiếng</p>\r\n\r\n<p>To&agrave;n m&agrave;n h&igrave;nh</p>\r\n\r\n<p>Quảng c&aacute;o sẽ c&oacute; thể hiển thị sau&nbsp;<strong>5</strong>&nbsp;gi&acirc;y</p>\r\n\r\n<p>Ph&uacute;c c&oacute; vai tr&ograve;&nbsp;cầm đầu,&nbsp;điều h&agrave;nh qu&aacute;n karaoke Pharaon XO. C&aacute;c bị c&aacute;o Đỗ Văn Giỏi, Nguyễn Phước Thịnh l&agrave; quản l&yacute;, gi&aacute;m s&aacute;t, c&oacute; nhiệm vụ đưa kh&aacute;ch v&agrave;o ph&ograve;ng v&agrave; chỉ đạo nh&acirc;n vi&ecirc;n phục vụ n&ecirc;n c&oacute; vai tr&ograve; thứ hai. Tuy nhi&ecirc;n, HĐXX cho rằng c&aacute;c bị c&aacute;o đều th&agrave;nh khẩn khai b&aacute;o, ăn năn hối cải, c&oacute; nhiều t&igrave;nh tiết giảm nhẹ n&ecirc;n sẽ xem x&eacute;t khi lượng h&igrave;nh.</p>\r\n\r\n<p>Từ đ&oacute;, HĐXX&nbsp;<strong>tuy&ecirc;n phạt Ph&uacute;c &quot;XO&quot; 12 năm t&ugrave; -&nbsp;</strong>mức cao nhất<strong>&nbsp;</strong><a href=\"https://vnexpress.net/phuc-xo-bi-de-nghi-10-12-nam-tu-4116642.html\">VKS đề nghị trước đ&oacute;</a>; buộc nộp phạt 50 triệu đồng; tịch thu to&agrave;n bộ 600 triệu đồng thu nhập bất ch&iacute;nh.&nbsp;7 đồng phạm của Ph&uacute;c lĩnh 7- 8 năm t&ugrave;.</p>\r\n\r\n<p>Trần Ngọc T&agrave;i (em Ph&uacute;c) lĩnh&nbsp;4 năm t&ugrave;; 3 nh&acirc;n vi&ecirc;n kh&aacute;c nhận 9 th&aacute;ng đến 1 năm 6 th&aacute;ng t&ugrave; về tội&nbsp;<em>T&agrave;ng trữ tr&aacute;i ph&eacute;p chất ma t&uacute;y;&nbsp;</em>bị c&aacute;o c&ograve;n lại bị phạt 9 th&aacute;ng t&ugrave; về tội&nbsp;<em>Kh&ocirc;ng tố gi&aacute;c tội phạm</em>.</p>\r\n\r\n<p><img alt=\"Phúc XO được các bị cáo dìu ra xe về trại, sau phiên tòa chiều 17/6. Ảnh: Quỳnh Trần.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/17/Phuc-XO-14-4839-1592384640.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Es982PVCf2Ms_mfTHnZVKw\" /></p>\r\n\r\n<p>Ph&uacute;c &quot;XO&quot; được c&aacute;c bị c&aacute;o d&igrave;u ra xe về trại, sau phi&ecirc;n t&ograve;a chiều 17/6. Ảnh: Quỳnh Trần.</p>\r\n\r\n<p><strong>Theo c&aacute;o buộc</strong>,&nbsp;nhằm tăng doanh thu v&agrave; l&ocirc;i k&eacute;o kh&aacute;ch cho qu&aacute;n, từ th&aacute;ng 11/2018, Ph&uacute;c cho kh&aacute;ch chơi ma t&uacute;y&nbsp;ở tầng hầm v&agrave; lầu 3, 4, 5. Chủ qu&aacute;n&nbsp;chỉ đạo nh&acirc;n vi&ecirc;n cung cấp c&aacute;c dụng cụ như ống h&uacute;t, dĩa, tăng chỉnh &acirc;m lượng nhạc... khi kh&aacute;ch c&oacute; nhu cầu.</p>\r\n\r\n<p>Cơ quan điều tra x&aacute;c định, từ khi hoạt động đến khi bị bắt Ph&uacute;c thu lợi khoảng 600 triệu đồng. Kh&aacute;m x&eacute;t nh&agrave; của Ph&uacute;c ở x&atilde; B&agrave; Điểm, huyện H&oacute;c M&ocirc;n, cảnh s&aacute;t t&igrave;m thấy trong ph&ograve;ng Trần Ngọc T&agrave;i c&oacute; 20 vi&ecirc;n thuốc lắc, 10 g&oacute;i ketamin...</p>\r\n\r\n<p>Trong ng&agrave;y x&eacute;t xử đầu ti&ecirc;n, Ph&uacute;c thừa nhận chỉ đạo nh&acirc;n vi&ecirc;n để kh&aacute;ch sử dụng ma t&uacute;y v&agrave; cung cấp dụng cụ khi c&oacute; y&ecirc;u cầu. Tuy nhi&ecirc;n, bị c&aacute;o cho rằng c&aacute;o trạng truy tố m&igrave;nh &quot;<a href=\"https://vnexpress.net/phuc-xo-tung-bi-khach-danh-gay-lung-vi-cam-choi-ma-tuy-4116532.html\">c&oacute; phần oan</a>&quot;. Việc cho kh&aacute;ch sử dụng ma t&uacute;y trong qu&aacute;n kh&ocirc;ng nhằm mục đ&iacute;ch thu lợi, m&agrave; v&igrave; Ph&uacute;c từng bị kh&aacute;ch đ&aacute;nh g&atilde;y 4 đốt sống lưng&nbsp;khi cấm họ &quot;bay&quot; n&ecirc;n về sau sợ, kh&ocirc;ng ngăn cản nữa.&nbsp;&nbsp;</p>\r\n\r\n<p>Hầu hết bị c&aacute;o kh&aacute;c đều thừa nhận h&agrave;nh vi như c&aacute;o trạng truy tố. Trong đ&oacute;, Nguyễn Thanh Xu&acirc;n nh&acirc;n vi&ecirc;n thu ng&acirc;n của qu&aacute;n thừa nhận việc c&oacute; t&iacute;nh phụ thu đối với những ph&ograve;ng kh&aacute;ch sử dụng ma t&uacute;y. Tr&ecirc;n&nbsp;h&oacute;a đơn t&iacute;nh tiền, nh&acirc;n vi&ecirc;n phục vụ sẽ phải ghi số ph&ograve;ng k&egrave;m th&ecirc;m k&yacute; hiệu chữ B. Mỗi ng&agrave;y c&oacute; từ 5 đến 8 ph&ograve;ng c&oacute; kh&aacute;ch sử dụng ma t&uacute;y thu lợi khoảng 3 đến 5 triệu đồng.&nbsp;</p>\r\n\r\n<ul>\r\n	<li><a href=\"https://vnexpress.net/phuc-xo-bi-de-nghi-10-12-nam-tu-4116642.html\">Ph&uacute;c &#39;XO&#39; bị đề nghị 10-12 năm t&ugrave;</a><a href=\"https://vnexpress.net/phuc-xo-bi-de-nghi-10-12-nam-tu-4116642.html#box_comment_vne\">&nbsp;78</a></li>\r\n	<li><a href=\"https://vnexpress.net/phuc-xo-tung-bi-khach-danh-gay-lung-vi-cam-choi-ma-tuy-4116532.html\">Ph&uacute;c &#39;XO&#39;: &#39;Từng bị kh&aacute;ch đ&aacute;nh g&atilde;y lưng v&igrave; cấm chơi ma t&uacute;y&#39;</a><a href=\"https://vnexpress.net/phuc-xo-tung-bi-khach-danh-gay-lung-vi-cam-choi-ma-tuy-4116532.html#box_comment_vne\">&nbsp;57</a></li>\r\n	<li><a href=\"https://vnexpress.net/phuc-xo-duoc-diu-den-toa-4116229.html\">Ph&uacute;c &#39;XO&#39; được d&igrave;u đến t&ograve;a</a><a href=\"https://vnexpress.net/phuc-xo-duoc-diu-den-toa-4116229.html#box_comment_vne\">&nbsp;140</a></li>\r\n	<li><a href=\"https://vnexpress.net/phuc-xo-va-11-nguoi-bi-bat-tam-giam-3913543.html\">Ph&uacute;c &#39;XO&#39; v&agrave; 11 người bị bắt tạm giam</a><a href=\"https://vnexpress.net/phuc-xo-va-11-nguoi-bi-bat-tam-giam-3913543.html#box_comment_vne\">&nbsp;237</a></li>\r\n</ul>\r\n\r\n<p><strong>Hải Duy&ecirc;n</strong></p>', 1, 1, 0, 1, 1, 'đời sống', NULL, NULL),
(9, 'vi', 'Phúc \'XO\' được dìu đến tòa', 'Hai chân bị teo cơ, Trần Ngọc Phúc, 37 tuổi - tức Phúc \"XO\", được hai đồng phạm dìu vào phòng xử, sáng 16/6.', 'upload/images/Phu-c-XO-1-7443-1592275486892.jpg', '2020-06-30', 0, '<p>Từ s&aacute;ng sớm, rất đ&ocirc;ng người th&acirc;n của c&aacute;c bị c&aacute;o&nbsp;c&oacute; mặt tại TAND TP HCM. Hơn 8h, Trần Ngọc Ph&uacute;c v&agrave; 7 đồng phạm được đưa đến,&nbsp;dẫn v&agrave;o bằng cửa sau. H&agrave;ng chục cảnh s&aacute;t, bảo vệ t&ograve;a được huy động để đảm bảo an ninh.</p>\r\n\r\n<p>Ph&uacute;c - chủ qu&aacute;n karaoke&nbsp;XO Pharaon, gầy hơn l&uacute;c bị bắt, hai ch&acirc;n bị teo cơ, kh&oacute;&nbsp;đi lại. Anh ta bị x&eacute;t xử&nbsp;về tội&nbsp;<em>Chứa chấp sử dụng tr&aacute;i ph&eacute;p chất ma tu&yacute;</em>&nbsp;theo khoản 2 Điều 256 BLHS năm 2015, khung h&igrave;nh phạt 7-15 năm t&ugrave;.</p>\r\n\r\n<p>Li&ecirc;n quan vụ &aacute;n, Trần Ngọc T&agrave;i, 24 tuổi (em ruột Ph&uacute;c) c&ugrave;ng 4 người bị truy tố tội&nbsp;<em>T&agrave;ng trữ tr&aacute;i ph&eacute;p chất ma tu&yacute;</em>&nbsp;v&agrave;&nbsp;<em>Kh&ocirc;ng tố gi&aacute;c tội phạm.</em></p>\r\n\r\n<p>Phi&ecirc;n t&ograve;a do thẩm ph&aacute;n Ng&ocirc; Ngọc Thắng l&agrave;m chủ tọa. Năm luật sư tham gia b&agrave;o chữa cho c&aacute;c bị c&aacute;o, ri&ecirc;ng Ph&uacute;c c&oacute; 3 người bảo vệ.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, đến 9h, thư k&yacute; t&ograve;a th&ocirc;ng b&aacute;o, phi&ecirc;n xử khai mạc muộn do gia đ&igrave;nh Ph&uacute;c từ chối một luật sư v&agrave; đề nghị ho&atilde;n phi&ecirc;n t&ograve;a nhưng HĐXX kh&ocirc;ng chấp nhận bởi&nbsp;bị c&aacute;o c&ograve;n hai người kh&aacute;c bảo vệ.</p>\r\n\r\n<p>Video Player is loading.</p>\r\n\r\n<p>Dừng</p>\r\n\r\n<p>Hiện tại&nbsp;0:06</p>\r\n\r\n<p>/</p>\r\n\r\n<p>Thời lượng&nbsp;1:33</p>\r\n\r\n<p>Đ&atilde; tải: 0%</p>\r\n\r\n<p>Tiến tr&igrave;nh: 0%</p>\r\n\r\n<p>Bỏ tắt tiếng</p>\r\n\r\n<p>To&agrave;n m&agrave;n h&igrave;nh</p>\r\n\r\n<p>Quảng c&aacute;o sẽ c&oacute; thể hiển thị sau&nbsp;<strong>4</strong>&nbsp;gi&acirc;y</p>\r\n\r\n<p>Ph&uacute;c &quot;XO&quot; v&agrave; đồng phạm được đưa v&agrave;o ph&ograve;ng x&eacute;t xử từ cửa sau, kh&ocirc;ng tiếp x&uacute;c với người nh&agrave;. Video:&nbsp;<em>Điệp Nguyễn.</em></p>\r\n\r\n<p>HĐXX lần lượt thẩm vấn c&aacute;c bị c&aacute;o, Ph&uacute;c l&agrave; người sau c&ugrave;ng. &quot;Dạ d&uacute;ng&quot;, chủ qu&aacute;n karaoke nhiều lần x&aacute;c nhận một số h&agrave;nh vi như c&aacute;o trạng truy tố, bằng giọng r&agrave;nh rọt, ngắn gọn.</p>\r\n\r\n<p>Trước đ&oacute;, Ph&uacute;c&nbsp;cho biết tinh thần kh&aacute; thoải m&aacute;i khi hầu t&ograve;a, song bị đau cột sống n&ecirc;n kh&ocirc;ng thể tự đi lại.&nbsp;HĐXX n&oacute;i thư k&yacute; lấy ghế tựa cho bị c&aacute;o ngồi, đảm bảo sức khỏe tham gia hết phi&ecirc;n t&ograve;a.</p>\r\n\r\n<p>C&aacute;c bị c&aacute;o l&agrave; nh&acirc;n vi&ecirc;n của Ph&uacute;c thừa nhận được &ocirc;ng chủ chỉ đạo trong c&aacute;c cuộc họp &quot;chuẩn bị dụng cụ cho kh&aacute;ch sử dụng ma t&uacute;y khi c&oacute; nhu&nbsp;cầu&quot;.</p>\r\n\r\n<p>Hai nữ nh&acirc;n vi&ecirc;n bị truy tố về tội<em>&nbsp;T&agrave;ng trữ tr&aacute;i ph&eacute;p chất ma t&uacute;y</em>&nbsp;được tại ngoại cho biết mới l&agrave;m cho Ph&uacute;c được khoảng 20 ng&agrave;y. Họ kh&ocirc;ng được trả lương, thu nhập từ&nbsp;tiền &quot;bo&quot; của kh&aacute;ch. Ma t&uacute;y trong t&uacute;i x&aacute;ch bị c&ocirc;ng an thu giữ do kh&aacute;ch h&aacute;t karaoke&nbsp;cho, nhưng kh&ocirc;ng nhớ l&agrave; ai.</p>\r\n\r\n<p>Chiều nay, đại diện VKS v&agrave; luật sư sẽ tham gia x&eacute;t hỏi.</p>\r\n\r\n<p><img alt=\"Chủ tọa Ngô Ngọc Thắng công bố quyết định đưa vụ án ra xét xử. Ảnh: Hữu Khoa.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/16/Ngo-Ngoc-Thang-3135-1592277300.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=DAXCdJOWXwHOks3NE_npGw\" /></p>\r\n\r\n<p>Chủ tọa Ng&ocirc; Ngọc Thắng c&ocirc;ng bố quyết định đưa vụ &aacute;n ra x&eacute;t xử. Ảnh:&nbsp;<em>Hữu Khoa.</em></p>\r\n\r\n<p><strong>Theo c&aacute;o trạng</strong>, Ph&uacute;c thu&ecirc; giấy ph&eacute;p kinh doanh qu&aacute;n karaoke XO Pharaon tr&ecirc;n đường Trường Chinh, quận 12, với gi&aacute; 20 triệu đồng mỗi th&aacute;ng, hoạt động từ th&aacute;ng 8/2018. Để tăng doanh thu v&agrave; l&ocirc;i k&eacute;o kh&aacute;ch cho qu&aacute;n, ba th&aacute;ng sau, Ph&uacute;c bắt đầu cho kh&aacute;ch sử dụng ma t&uacute;y khi đến đ&acirc;y vui chơi.&nbsp;Nhiều người được thu&ecirc; để&nbsp;quản l&yacute;, gi&aacute;m s&aacute;t v&agrave; phục vụ.</p>\r\n\r\n<p>Ph&uacute;c &quot;kh&ocirc;ng cho ph&eacute;p nh&acirc;n vi&ecirc;n b&aacute;n ma t&uacute;y hay đi mua ma t&uacute;y gi&uacute;p kh&aacute;ch&quot;,&nbsp;chỉ cung cấp c&aacute;c dụng cụ như ống h&uacute;t, dĩa... v&agrave; tăng chỉnh &acirc;m lượng nhạc khi kh&aacute;ch c&oacute; nhu cầu.&nbsp;</p>\r\n\r\n<p>Kh&aacute;ch sử dụng dịch vụ được t&iacute;nh tiền bằng mật m&atilde;. Đối với kh&aacute;ch thu&ecirc; ph&ograve;ng h&aacute;t karaoke b&igrave;nh thường, qu&aacute;n t&iacute;nh gi&aacute; như ni&ecirc;m yết, tr&ecirc;n h&oacute;a đơn thanh to&aacute;n ghi t&ecirc;n ph&ograve;ng th&ecirc;m chữ &quot;A&quot;. Kh&aacute;ch thu&ecirc; ph&ograve;ng để sử dụng ma t&uacute;y phải trả phụ thu 200.000 đồng một giờ v&agrave; tr&ecirc;n h&oacute;a đơn ghi chữ &quot;B&quot;. Trường hợp kh&aacute;ch sử dụng b&agrave;n DJ v&agrave; nh&acirc;n vi&ecirc;n chỉnh &acirc;m lượng th&igrave; thu th&ecirc;m 400.000 đồng mỗi tiếng. Nh&oacute;m kh&aacute;ch VIP n&agrave;y được bố tr&iacute; chơi ở tầng hầm v&agrave; lầu 3, 4, 5.</p>\r\n\r\n<p>Cơ quan điều tra x&aacute;c định, từ khi hoạt động đến khi bị bắt Ph&uacute;c thu lợi khoảng 600 triệu đồng.</p>\r\n\r\n<p><img alt=\"Chủ quán karaoke XO Pharaon trông gầy hơn rất nhiều so với lúc bị bắt tháng 4/2019. Ảnh: Hữu Khoa.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/16/Phu-c-XO-2-8746-1592277301.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=OvxrJf_QgE3ma9MkY5VLkg\" /></p>\r\n\r\n<p>Chủ qu&aacute;n karaoke&nbsp;XO Pharaon tr&ocirc;ng gầy hơn rất nhiều so với l&uacute;c bị bắt th&aacute;ng 4/2019. Ảnh:&nbsp;<em>Hữu Khoa.</em></p>\r\n\r\n<p>&quot;Động lắc&quot; của Ph&uacute;c bị&nbsp;Ph&ograve;ng Cảnh s&aacute;t H&igrave;nh sự (PC02) C&ocirc;ng an TP HCM đột k&iacute;ch rạng s&aacute;ng 10/4/2019,&nbsp;bắt quả tang gần 100 người ph&ecirc; ma t&uacute;y ở 5 ph&ograve;ng, trong đ&oacute; c&oacute; 30 nh&acirc;n vi&ecirc;n karaoke.&nbsp;</p>\r\n\r\n<p>Kh&aacute;m x&eacute;t nh&agrave; của Ph&uacute;c ở x&atilde; B&agrave; Điểm, huyện H&oacute;c M&ocirc;n, cảnh s&aacute;t t&igrave;m thấy trong ph&ograve;ng Trần Ngọc T&agrave;i c&oacute; 20 vi&ecirc;n thuốc lắc, 10 g&oacute;i ketamin; s&uacute;ng c&ocirc;ng cụ hỗ trợ, đạn, kiếm, b&igrave;nh xịt hơi cay, &aacute;o gi&aacute;p...</p>\r\n\r\n<p>Phi&ecirc;n t&ograve;a dự kiến k&eacute;o d&agrave;i đến 17/6.</p>\r\n\r\n<ul>\r\n	<li><a href=\"https://vnexpress.net/phuc-xo-bi-truy-to-cao-nhat-15-nam-tu-4051669.html\">Ph&uacute;c &#39;XO&#39; bị truy tố cao nhất 15 năm t&ugrave;</a><a href=\"https://vnexpress.net/phuc-xo-bi-truy-to-cao-nhat-15-nam-tu-4051669.html#box_comment_vne\">&nbsp;10</a></li>\r\n	<li><a href=\"https://vnexpress.net/phuc-xo-khai-deo-vang-gia-de-quang-cao-viec-kinh-doanh-3908385.html\">Ph&uacute;c &#39;XO&#39; khai đeo v&agrave;ng giả để quảng c&aacute;o việc kinh doanh</a><a href=\"https://vnexpress.net/phuc-xo-khai-deo-vang-gia-de-quang-cao-viec-kinh-doanh-3908385.html#box_comment_vne\">&nbsp;537</a></li>\r\n	<li><a href=\"https://vnexpress.net/phuc-xo-to-chuc-su-dung-ma-tuy-tai-karaoke-nhu-the-nao-3909180.html\">Ph&uacute;c &#39;XO&#39; tổ chức sử dụng ma tu&yacute; tại karaoke như thế n&agrave;o</a><a href=\"https://vnexpress.net/phuc-xo-to-chuc-su-dung-ma-tuy-tai-karaoke-nhu-the-nao-3909180.html#box_comment_vne\">&nbsp;196</a></li>\r\n</ul>\r\n\r\n<p><strong>Hải Duy&ecirc;n</strong></p>', 1, 1, 0, 1, 1, 'đời sống', NULL, NULL);
INSERT INTO `tin` (`idTin`, `lang`, `TieuDe`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`, `tags`, `created_at`, `updated_at`) VALUES
(10, 'vi', 'Bị cáo khai \'bị ép cung 7 ngày đêm\'', 'Vương Văn Hùng khai \"suốt 7 ngày 7 đêm bị ép cung\", không được cho ngủ nên mới phải nhận tội trong vụ án sát hại thiếu nữ giao gà.\r\n\r\nTrả lời thẩm vấn sáng 16/6 tại phiên phúc thẩm, Vương Văn Hùng (36 tuổi) phản bác việc án sơ thẩm quy kết anh ta phạm tội Giết người, Hiếp dâm. Văn Hùng cho rằng bị oan vì chỉ tham gia Bắt cóc nhằm chiếm đoạt tài sản chứ không cùng thực hiện các hành vi phạm tội khác.', 'upload/images/IMG-8529-JPG-4299-1592282406651.jpg', '2020-06-21', 0, '<p>Văn H&ugrave;ng khai trong qu&aacute; tr&igrave;nh điều tra đ&atilde; &quot;bị bức cung, d&ugrave;ng nhục h&igrave;nh&quot;. &quot;C&oacute; đợt t&ocirc;i bị &eacute;p cung suốt 7 ng&agrave;y 7 đ&ecirc;m, kh&ocirc;ng được cho ngủ. T&ocirc;i c&ograve;n bị lột hết quần &aacute;o, d&ugrave;ng d&ugrave;i cui điện d&iacute; v&agrave;o người n&ecirc;n buộc phải nhận tội&quot;, Văn H&ugrave;ng n&oacute;i.</p>\r\n\r\n<p>&quot;Qu&aacute; tr&igrave;nh điều tra vi&ecirc;n lấy lời khai bị c&aacute;o c&oacute; luật sư, kiểm s&aacute;t vi&ecirc;n c&oacute;i c&ugrave;ng tham gia kh&ocirc;ng?&quot;,&nbsp;chủ toạ hỏi. Văn H&ugrave;ng đ&aacute;p &quot;to&agrave;n bị đ&aacute;nh đập trước khi lấy lời khai&quot;.</p>\r\n\r\n<p>Khi VKS đến ph&uacute;c cung, Văn H&ugrave;ng đ&atilde; tr&igrave;nh b&agrave;y về việc bị &eacute;p cung nhưng kiểm s&aacute;t vi&ecirc;n kh&ocirc;ng nghe v&agrave; bảo &quot;đợi sau rồi tr&igrave;nh b&agrave;y&quot;. Văn H&ugrave;ng c&ograve;n cho rằng rất nhiều bản cung chỉ được k&yacute; chứ kh&ocirc;ng được nghe điều tra vi&ecirc;n đọc. &quot;C&oacute; lần, điều tra vi&ecirc;n c&ograve;n đưa cả 6-7 &quot;tờ giấy lấy cung&quot; bắt bị c&aacute;o k&yacute; khống. Bị c&aacute;o y&ecirc;u cầu viết lời khai v&agrave;o đ&oacute; rồi mới k&yacute; song kh&ocirc;ng được chấp nhận&quot;, Văn H&ugrave;ng tr&igrave;nh b&agrave;y tại phi&ecirc;n ph&uacute;c thẩm.</p>\r\n\r\n<p><img alt=\"Bị cáo Vương Văn Hùng. Ảnh: Phạm Dự.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/16/IMG-8529-JPG-4299-1592282406.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=rMVwB1OffOjTUXyPN47oZA\" /></p>\r\n\r\n<p>Bị c&aacute;o Vương Văn H&ugrave;ng. Ảnh:&nbsp;<em>Phạm Dự.</em></p>\r\n\r\n<p>Văn H&ugrave;ng n&oacute;i&nbsp;lời khai của nhiều bị can kh&aacute;c trong vụ &aacute;n kh&ocirc;ng đ&uacute;ng. Ng&agrave;y m&ugrave;ng 2 v&agrave; 3 Tết, anh ta ở nh&agrave; cả ng&agrave;y chứ kh&ocirc;ng tham gia cưỡng bức, s&aacute;t hại thiếu nữ giao g&agrave; như lời khai của c&aacute;c bị c&aacute;o. M&ugrave;ng 4 Tết, Văn H&ugrave;ng mới đến nh&agrave; To&aacute;n ch&uacute;c Tết. Văn H&ugrave;ng đề nghị HĐXX tr&iacute;ch xuất dữ liệu camera ở đối diện nh&agrave; m&igrave;nh để chứng minh h&agrave;nh vi ngoại phạm.</p>\r\n\r\n<p>Luật sư do to&agrave; chỉ định của&nbsp;Văn H&ugrave;ng sau đ&oacute; cho rằng qu&aacute; tr&igrave;nh l&agrave;m việc trong trại giam kh&ocirc;ng thấy th&acirc;n chủ&nbsp;th&ocirc;ng b&aacute;o về việc bị &eacute;p cung. Tuy nhi&ecirc;n, luật sư sẽ l&agrave;m việc lại với VKS v&agrave; luật sư để l&agrave;m r&otilde; vấn đề n&agrave;y.</p>\r\n\r\n<p>Trước lời khai của Văn H&ugrave;ng, chủ&nbsp;toạ cho hay luật sư v&agrave; kiểm s&aacute;t vi&ecirc;n đ&atilde; n&oacute;i kh&ocirc;ng được nh&igrave;n thấy&nbsp;việc bị c&aacute;o bị bức cung. &quot;Vậy bị c&aacute;o c&oacute; đơn kh&aacute;ng nghị chưa?&quot;, chủ toạ hỏi. Văn H&ugrave;ng khẳng định&nbsp;đ&atilde; l&agrave;m đơn nhưng kh&ocirc;ng được gửi đi. Tuy nhi&ecirc;n trong đơn kh&aacute;ng c&aacute;o Văn H&ugrave;ng&nbsp;viết hết về việc từng bị bức cung nhiều lần.</p>\r\n\r\n<p>Trả lời sau Văn H&ugrave;ng, Phạm Văn Nhiệm cũng cho rằng bị oan, kh&ocirc;ng phạm tội&nbsp;<em>Giết người, Hiếp d&acirc;m, Bắt c&oacute;c nhằm chiếm đoạt t&agrave;i sản</em>&nbsp;như bản &aacute;n sơ thẩm quy kết. Nhiệm cũng khai từng bị điều tra vi&ecirc;n &eacute;p cung, mớm cung nhiều lần n&ecirc;n mới nhận tội. C&oacute; lần, anh ta c&ograve;n phải ch&eacute;p lời khai v&agrave;o bản tự khai do điều tra vi&ecirc;n đọc.</p>\r\n\r\n<p>Theo bản &aacute;n sơ thẩm, Văn H&ugrave;ng v&agrave; Nhiệm&nbsp;đều bị tuy&ecirc;n phạt tử h&igrave;nh về ba tội&nbsp;<em>Giết người, Hiếp d&acirc;m, Bắt c&oacute;c nhằm chiếm đoạt t&agrave;i sản.</em></p>\r\n\r\n<p><img alt=\"Bị cáo Vì Văn Toán. Ảnh: Phạm Dự.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/16/IMG-8609-JPG-1880-1592282406.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=NkXMzkU350givzIAjjjCtA\" /></p>\r\n\r\n<p>Bị c&aacute;o V&igrave; Văn To&aacute;n. Ảnh:&nbsp;<em>Phạm Dự.</em></p>\r\n\r\n<p>Tr&aacute;i với c&aacute;c đồng phạm,&nbsp;bị c&aacute;o V&igrave; Văn To&aacute;n (38 tuổi) thừa nhận phạm tội&nbsp;<em>Giết người, Bắt c&oacute;c nhằm chiếm đoạt t&agrave;i sản</em>&nbsp;như bản &aacute;n sơ thẩm quy kết. To&aacute;n kh&aacute;ng c&aacute;o để xin giảm nhẹ h&igrave;nh phạt bởi chỉ&nbsp;thực hiện h&agrave;nh vi phạm tội khi đi đ&ograve;i nợ tiền mua b&aacute;n ma tu&yacute;, kh&ocirc;ng trực tiếp s&aacute;t hại thiếu nữ giao g&agrave;.</p>\r\n\r\n<p>To&aacute;n khai&nbsp;năm 2009, b&agrave; Trần Thị Hiền (mẹ nạn nh&acirc;n) đến nh&agrave; To&aacute;n mua hai con g&agrave; v&agrave; đặt vấn đề mua hai b&aacute;nh heroin. To&aacute;n&nbsp;b&aacute;n&nbsp;hai b&aacute;nh ma tu&yacute; với gi&aacute; 300 triệu đồng song chưa lấy được tiền. Gần một năm sau, To&aacute;n bị bắt trong&nbsp;vụ &aacute;n kh&aacute;c v&agrave; bị phạt 9 năm 6 th&aacute;ng t&ugrave;.</p>\r\n\r\n<p>Ng&agrave;y 24/5/2017, To&aacute;n m&atilde;n hạn t&ugrave; song kh&ocirc;ng đi đ&ograve;i tiền. Đầu năm 2019, To&aacute;n đến chợ Mường Thanh, th&agrave;nh phố Điện Bi&ecirc;n Phủ nơi b&agrave; Hiền đang b&aacute;n g&agrave; để đ&ograve;i nợ. To&aacute;n sau đ&oacute; đi theo b&agrave; Hiền từ chợ Mường Thanh đến c&aacute;nh đồng x&atilde; Thanh Hưng, huyện Điện Bi&ecirc;n th&igrave; chặn đầu xe, n&oacute;i: &quot;Sao chị kh&ocirc;ng trả tiền. Em cũng đang phải đi nợ người ta&quot;. B&agrave; Hiền trả lời&nbsp;tiền &quot;b&aacute;n h&agrave;ng&quot;&nbsp;cũng chưa đ&ograve;i được.</p>\r\n\r\n<p>&quot;Tại sao kinh tế kh&oacute; khăn m&agrave; gần 2 năm kể từ khi ra t&ugrave; bị c&aacute;o mới đi đ&ograve;i nợ?&quot;, chủ toạ truy vấn. To&aacute;n đ&aacute;p đ&atilde; đến nh&agrave; b&agrave; Hiền một lần song thấy nh&agrave; kho&aacute; cửa n&ecirc;n nghĩ b&agrave; ta đ&atilde; bị c&ocirc;ng an bắt. Cuối năm 2018, To&aacute;n mới biết b&agrave; Hiền vẫn &quot;ngo&agrave;i v&ograve;ng ph&aacute;p luật&quot; v&agrave; h&agrave;ng ng&agrave;y đang đi b&aacute;n g&agrave; ngo&agrave;i chợ n&ecirc;n nảy sinh &yacute; định đ&ograve;i nợ.</p>\r\n\r\n<p>To&aacute;n khai do t&uacute;ng quẫn đ&atilde; b&agrave;n với&nbsp;Văn H&ugrave;ng (bạn c&ugrave;ng trại giam Y&ecirc;n Hạ) về kế hoạch bắt c&oacute;c b&agrave; Hiền. Ng&agrave;y 1/2/2019,&nbsp;Văn H&ugrave;ng v&agrave; B&ugrave;i Văn C&ocirc;ng đến nh&agrave; &quot;chơi&quot; ma t&uacute;y n&ecirc;n To&aacute;n n&oacute;i việc b&agrave; Hiền đang nợ tiền v&agrave; nhờ&nbsp;đ&ograve;i hộ, hứa trả c&ocirc;ng 50 triệu đồng.</p>\r\n\r\n<p>&quot;Bị c&aacute;o l&ecirc;n kế hoạch bắt c&oacute;c b&agrave; mẹ nhưng sao lại nhằm v&agrave;o con g&aacute;i&quot;, chủ toạ hỏi. To&aacute;n đ&aacute;p mục đ&iacute;ch ban đầu l&agrave; g&acirc;y sức &eacute;p để b&agrave; Hiền trả tiền. Tuy nhi&ecirc;n sau đ&oacute; C&ocirc;ng &quot;hiến kế&quot; l&agrave; phải bắt c&oacute;c con g&aacute;i. To&aacute;n&nbsp;t&aacute;n th&agrave;nh.</p>\r\n\r\n<p>Theo kế hoạch, chiều 4/2/2019 (30 Tết Nguy&ecirc;n đ&aacute;n 2019), Văn H&ugrave;ng gọi điện cho con g&aacute;i b&agrave; Hiền đặt mua 13 con g&agrave; v&agrave; y&ecirc;u cầu giao h&agrave;ng đến khu vực đồi Độc Lập, th&agrave;nh phố Điện Bi&ecirc;n Phủ. C&ocirc; g&aacute;i đến nơi th&igrave; bị&nbsp;Văn H&ugrave;ng, C&ocirc;ng, Phạm Văn Nhiệm, Lường Văn Lả, Lường Văn H&ugrave;ng<strong>&nbsp;</strong>khống chế.</p>\r\n\r\n<p>Từ đ&ecirc;m 30 Tết đến tối 6/2/2019 (m&ugrave;ng 2 Tết), nạn nh&acirc;n bị nhốt trong nh&agrave; C&ocirc;ng ở x&atilde; Thanh Nưa, huyện Điện Bi&ecirc;n. C&aacute;c bị can C&ocirc;ng, Dũng, Lả, Nhiệm, Vương Văn H&ugrave;ng v&agrave; Lường Văn H&ugrave;ng đ&atilde; d&ugrave;ng vũ lực để quan hệ t&igrave;nh dục tr&aacute;i &yacute; muốn với nữ sinh 5 lần ngay tại nh&agrave; C&ocirc;ng. Trong đ&oacute;, C&ocirc;ng l&agrave; người khởi xướng v&agrave; thực hiện đầu ti&ecirc;n.</p>\r\n\r\n<p>Sau khi nh&oacute;m C&ocirc;ng đưa &quot;con tin&quot; đi, Vương Văn H&ugrave;ng l&aacute;i xe m&aacute;y chở lồng g&agrave; đến v&agrave; đưa cho To&aacute;n chiếc điện thoại di động. To&aacute;n thấy điện thoại rung khoảng 8 lần song kh&ocirc;ng nghe.</p>\r\n\r\n<p>Đến bến xe Bản Phủ, To&aacute;n dừng xe để nghe điện thoại, thấy đầu d&acirc;y b&ecirc;n kia c&oacute; giọng con g&aacute;i nghĩ l&agrave; người nh&agrave; của con tin n&ecirc;n y&ecirc;u cầu gặp b&agrave; Hiền. Sau khi b&agrave; Hiền nhấc m&aacute;y, To&aacute;n n&oacute;i: &quot;Em đ&atilde; bắt c&oacute;c con g&aacute;i. Chị sắp xếp trả tiền đi&quot;. Đầu d&acirc;y b&ecirc;n kia, b&agrave; Hiền đ&aacute;p: &quot;Sao ch&uacute;ng m&agrave;y bắt c&oacute;c con g&aacute;i tao. Ch&uacute;ng m&agrave;y kh&ocirc;ng thả tao sẽ b&aacute;o c&ocirc;ng an&quot;. To&aacute;n sau đ&oacute; c&uacute;p m&aacute;y v&agrave; trả điện thoại cho Văn H&ugrave;ng.</p>\r\n\r\n<p>Khoảng 23h&nbsp;m&ugrave;ng 2 Tết, To&aacute;n đ&eacute;n&nbsp;nh&agrave; C&ocirc;ng th&igrave; thấy nạn nh&acirc;n sức khỏe yếu. Nhiệm đề xuất đưa đi cấp cứu song C&ocirc;ng gạt đi v&igrave; sẽ kh&ocirc;ng kh&aacute;c g&igrave; tự b&aacute;o c&ocirc;ng an. Khi cả nh&oacute;m chưa c&oacute; phương &aacute;n, C&ocirc;ng n&oacute;i &quot;để tao giải quyết&quot;.</p>\r\n\r\n<p><img alt=\"Bị cáo Bùi Văn Công. Ảnh: Phạm Dự.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/16/IMG-8501-JPG-1431-1592282407.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ENqbpwnxXNJjoqUyV8ycig\" /></p>\r\n\r\n<p>Bị c&aacute;o B&ugrave;i Văn C&ocirc;ng. Ảnh:&nbsp;<em>Phạm Dự.</em></p>\r\n\r\n<p>&quot;Ai l&agrave; người khởi xướng giết nạn nh&acirc;n?&quot;, HĐXX hỏi. To&aacute;n khai&nbsp;C&ocirc;ng l&agrave; người khởi xướng giết con g&aacute;i b&agrave; Hiền v&agrave; anh ta c&ugrave;ng những người c&oacute; mặt đ&atilde; đồng &yacute;. Khi C&ocirc;ng s&aacute;t hại con tin, To&aacute;n đứng c&aacute;ch đ&oacute; chừng 5 m&eacute;t v&agrave; c&oacute; cả B&ugrave;i Thị Kim Thu (vợ C&ocirc;ng) chứng kiến. Sau đ&oacute; cả nh&oacute;m b&agrave;n kế hoạch che giấu cảnh s&aacute;t v&agrave; &quot;ai về nh&agrave; nấy&quot;.</p>\r\n\r\n<p>Theo To&aacute;n, B&ugrave;i Thị Kim Thu chứng kiến to&agrave;n bộ sự việc song kh&ocirc;ng can ngăn. Ng&agrave;y 6/2/2019, Thu vờ ph&aacute;t hiện x&aacute;c nạn nh&acirc;n v&agrave; tr&igrave;nh b&aacute;o cảnh s&aacute;t.</p>\r\n\r\n<p>Phi&ecirc;n to&agrave; đang tiếp tục l&agrave;m việc.</p>\r\n\r\n<ul>\r\n	<li><a href=\"https://vnexpress.net/ba-nguoi-keu-oan-trong-vu-an-sat-hai-thieu-nu-giao-ga-4111101.html\">Ba người k&ecirc;u oan trong vụ &aacute;n s&aacute;t hại thiếu nữ giao g&agrave;</a><a href=\"https://vnexpress.net/ba-nguoi-keu-oan-trong-vu-an-sat-hai-thieu-nu-giao-ga-4111101.html#box_comment_vne\">&nbsp;46</a></li>\r\n	<li><a href=\"https://vnexpress.net/bac-si-phap-y-giai-ma-cai-chet-cua-thieu-nu-giao-ga-4060799.html\">B&aacute;c sĩ ph&aacute;p y giải m&atilde; c&aacute;i chết của thiếu nữ giao g&agrave;</a><a href=\"https://vnexpress.net/bac-si-phap-y-giai-ma-cai-chet-cua-thieu-nu-giao-ga-4060799.html#box_comment_vne\">&nbsp;86</a></li>\r\n</ul>\r\n\r\n<p><strong>Phạm Dự</strong></p>', 5, 21, 0, 1, 1, 'sản phẩm', NULL, NULL),
(11, 'vi', 'Nghi ngại nguồn lây nCoV từ thực phẩm sống', 'Sau khi phát hiện nCoV trên thớt thái cá hồi sống nhập khẩu, chuyên gia y tế cảnh báo người dân không ăn sống loại cá này cho đến khi có kết quả điều tra rõ ràng.', 'upload/images/dbc25cc6312b417bbc42c7a8cc7347-4816-9876-1592361879386.jpg', '2020-06-26', 0, '<p>Sự việc l&agrave;m dấy l&ecirc;n c&acirc;u hỏi liệu thực phẩm đ&ocirc;ng lạnh c&oacute; thể trở th&agrave;nh nơi cư tr&uacute; v&agrave; l&acirc;y lan&nbsp;của nCoV hay kh&ocirc;ng.&nbsp;</p>\r\n\r\n<p>&Ocirc;ng Wu Zunyou, gi&aacute;m đốc dịch tễ tại Trung t&acirc;m Kiểm so&aacute;t v&agrave; Ph&ograve;ng ngừa Dịch bệnh Trung Quốc (CDC), cho biết virus c&oacute; thể tồn tại tr&ecirc;n bề mặt đồ ăn đ&ocirc;ng lạnh khoảng ba th&aacute;ng. Cơ quan cũng đặt ra giả thuyết mầm bệnh được &quot;nhập khẩu&quot; v&agrave;o nước n&agrave;y qua h&agrave;ng h&oacute;a bị nhiễm bẩn.&nbsp;</p>\r\n\r\n<p>Tiến sĩ Dale Fisher, chuy&ecirc;n gia về bệnh truyền nhiễm, gi&aacute;m đốc Mạng lưới Cảnh b&aacute;o v&agrave; Giải quyết Khủng hoảng Y tế To&agrave;n cầu tại Singapore, trực thuộc Tổ chức Y tế Thế giới (WHO),&nbsp;đồng t&igrave;nh với quan điểm n&agrave;y.&nbsp;</p>\r\n\r\n<p>&quot;Muốn bảo quản một loại virus, ta phải trữ đ&ocirc;ng n&oacute;. Như vậy, nếu mầm bệnh cư tr&uacute; trong c&aacute;c g&oacute;i h&agrave;ng h&oacute;a đ&ocirc;ng lạnh, ch&uacute;ng c&oacute; thể sống s&oacute;t. Thời gian th&ocirc;ng thường l&agrave; dưới một tuần. Nhưng nhiệt độ c&agrave;ng thấp, ch&uacute;ng c&agrave;ng tồn tại l&acirc;u hơn. Đ&acirc;y l&agrave; một phần của giả thuyết cho rằng Covid-19 dễ l&acirc;y lan hơn v&agrave;o m&ugrave;a đ&ocirc;ng&quot;.</p>\r\n\r\n<p><img alt=\"Ngày 12/6, các chuyên gia đã tìm thấy nCoV trên một chiếc thớt thái cá hồi nhập khẩu tại chợ Tân Phát Địa. Ảnh: CGTN\" src=\"https://i1-suckhoe.vnecdn.net/2020/06/17/dbc25cc6312b417bbc42c7a8cc7347-4816-9876-1592361879.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=A1Sjtwoq6eRNmQjX-Fd0vg\" /></p>\r\n\r\n<p>Ng&agrave;y 12/6, c&aacute;c chuy&ecirc;n gia đ&atilde; t&igrave;m thấy nCoV tr&ecirc;n một chiếc thớt th&aacute;i c&aacute; hồi nhập khẩu tại chợ T&acirc;n Ph&aacute;t Địa. Ảnh:&nbsp;<em>CGTN</em></p>\r\n\r\n<p>Tuy nhi&ecirc;n, một số chuy&ecirc;n gia c&oacute; &yacute; kiến ngược lại.&nbsp;</p>\r\n\r\n<p>C&aacute;c loại virus l&acirc;y lan qua thực phẩm đ&ocirc;ng lạnh thường g&acirc;y ra bệnh đường ti&ecirc;u h&oacute;a như&nbsp; ti&ecirc;u chảy cấp, vi&ecirc;m gan A, khi người bệnh ăn phải ch&uacute;ng. Theo Gi&aacute;o sư Jin Dong-Yun, khoa vi sinh, trường Đại học Hong Kong, hiện chưa c&oacute; bằng chứng nCoV l&acirc;y truyền qua việc ti&ecirc;u thụ thực phẩm bị nhiễm bẩn. Phần lớn người d&acirc;n mắc Covid-19 sau khi tiếp x&uacute;c với bệnh nh&acirc;n.&nbsp;</p>\r\n\r\n<p>Eual Leshem, gi&aacute;m đốc Trung t&acirc;m Y tế Du lịch v&agrave; Bệnh nhiệt đới, Trung t&acirc;m Y tế Sheba, Israel, nhận định: &quot;Trường hợp nhiễm tr&ugrave;ng từ thực phẩm tươi sống như thịt, c&aacute; chỉ c&oacute; thể xảy ra nếu một đầu bếp hoặc nh&acirc;n vi&ecirc;n phục vụ mắc bệnh chạm v&agrave;o thức ăn&quot;.&nbsp;</p>\r\n\r\n<p>Trước đ&oacute;, một nh&oacute;m nh&agrave; khoa học tại Hong Kong đ&atilde; chỉ ra rằng thời gian tồn tại của nCoV thay đổi đ&aacute;ng kể bởi nhiệt độ v&agrave; độ ẩm. B&aacute;o c&aacute;o sơ bộ cho thấy virus chỉ c&oacute; thể sống s&oacute;t một ng&agrave;y trong điều kiện 37 độ C. Nếu mức nhiệt giảm c&ograve;n 22 độ C, mầm bệnh lưu lại khoảng một tuần. Với 4 độ C, thời gian sống s&oacute;t của ch&uacute;ng hơn hai tuần.&nbsp;Tuy nhi&ecirc;n, nghi&ecirc;n cứu được thực hiện tr&ecirc;n bề mặt chất rắn, chứ kh&ocirc;ng phải thức ăn.&nbsp;</p>\r\n\r\n<p>&quot;T&ocirc;i kh&aacute; ho&agrave;i nghi về giả thiết cụm dịch mới thực sự bắt nguồn từ một loại thực phẩm đ&ocirc;ng lạnh c&aacute;ch xa h&agrave;ng ngh&igrave;n dặm. Nhiều khả năng, mầm bệnh xuất ph&aacute;t từ một người d&acirc;n l&agrave;m việc trong chợ. Nhưng ch&uacute;ng t&ocirc;i chưa thể đưa ra kết luận chắc chắn cho đến khi t&igrave;m được người&nbsp;n&agrave;y&quot;, Paul Tambuah, chủ tịch Viện Vi tr&ugrave;ng học v&agrave; Bệnh truyền nhiễm ch&acirc;u &Aacute; Th&aacute;i B&igrave;nh Dương, n&oacute;i.&nbsp;</p>\r\n\r\n<p>Tr&ecirc;n thực tế, c&aacute;c vật chủ tiềm năng của nCoV thường l&agrave; động vật c&oacute; v&uacute;. C&aacute; hồi gần như kh&ocirc;ng thể nhiễm virus, theo Cheng Gong, chuy&ecirc;n gia virus tại Đại học Thanh Hoa, Trung Quốc.&nbsp;</p>\r\n\r\n<p>&quot;Virus phải dựa v&agrave;o thụ thể tr&ecirc;n bề mặt tế b&agrave;o chủ để l&acirc;y nhiễm cho tế b&agrave;o. Nếu kh&ocirc;ng c&oacute; thụ thể nhất định, ch&uacute;ng kh&ocirc;ng thể x&acirc;m nhập th&agrave;nh c&ocirc;ng. C&aacute;c bằng chứng từ trước đến nay cho thấy những loại thụ thể n&agrave;y chỉ tồn tại ở động vật c&oacute; v&uacute;, kh&ocirc;ng phải c&aacute;&quot;, &ocirc;ng giải th&iacute;ch.</p>\r\n\r\n<p>Ưu ti&ecirc;n&nbsp; hiện nay của Bắc Kinh l&agrave; t&igrave;m cho ra nguồn l&acirc;y nhiễm cho c&aacute;i thớt th&aacute;i c&aacute;, &ocirc;ng Zhong Kai, chuy&ecirc;n gia thuộc Hiệp hội Y tế dự ph&ograve;ng Trung Quốc, n&oacute;i. &Ocirc;ng cũng khuyến c&aacute;o c&ocirc;ng ch&uacute;ng kh&ocirc;ng ăn c&aacute; hồi sống chừng n&agrave;o chưa c&oacute; kết quả điều tra nguồn gốc dịch.&nbsp;</p>\r\n\r\n<p><strong>Thục Linh&nbsp;</strong>(Theo<em>&nbsp;Reuters, CGTN</em>)</p>', 2, 6, 0, 1, 1, 'âm nhạc', NULL, NULL),
(12, 'vi', 'Chủ quán karaoke tổ chức cho khách chơi ma túy', 'Đàm Văn Trường, 34 tuổi, quê Nam Định, cùng nam nhân viên quán karaoke bị bắt khi tổ chức cho khách chơi ma túy.\r\n\r\nNgày 17/6, Trường và Hoàng Bá Phú (29 tuổi, quê Hà Tĩnh) bị Công an TP Biên Hòa tạm giữ hình sự để điều tra hành vi Tổ chức sử dụng ma túy trái phép.', 'upload/images/ma-tuy-8689-1592364260812.jpg', '2020-06-22', 0, '<p>Ba h&ocirc;m trước, cảnh s&aacute;t&nbsp;ập v&agrave;o qu&aacute;n karaoke 179 (TP Bi&ecirc;n H&ograve;a), bắt quả tang h&agrave;ng chục nam nữ thanh ni&ecirc;n nh&uacute;n&nbsp;nhảy trong tiếng nhạc xập x&igrave;nh. Hai&nbsp;đĩa sứ chứa bột ma t&uacute;y được ph&aacute;t hiện. Qua x&eacute;t nghiệm, ch&iacute;n người dương t&iacute;nh&nbsp;ma t&uacute;y.</p>\r\n\r\n<p>Trường khai nhận,&nbsp;khi kh&aacute;ch c&oacute; nhu cầu bay lắc, nh&acirc;n vi&ecirc;n sẽ bố tr&iacute; ph&ograve;ng cho kh&aacute;ch vui chơi v&agrave; sử dụng ma t&uacute;y. Ph&iacute; dịch vụ 300.000&nbsp;đồng mỗi giờ một ph&ograve;ng.</p>\r\n\r\n<p><img alt=\"Khách chơi ma túy do Trường tổ chức tại trụ sở Công an TP Biên Hòa. Ảnh: Thái Hà.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/17/ma-tuy-3816-1592366981.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=8DdNuas9nlDmv0bCO9MCOg\" /></p>\r\n\r\n<p>Kh&aacute;ch chơi ma t&uacute;y do Trường tổ chức tại trụ sở C&ocirc;ng an TP Bi&ecirc;n H&ograve;a. Ảnh:<em>&nbsp;Th&aacute;i H&agrave;.</em></p>\r\n\r\n<p>Tối 16/6, C&ocirc;ng an Đồng Nai cũng ph&aacute;t hiện&nbsp;25 người&nbsp;dương t&iacute;nh ma t&uacute;y tại qu&aacute;n karaoke ở x&atilde; T&acirc;y H&ograve;a, huyện Trảng Bom.</p>\r\n\r\n<p><strong>Ho&agrave;ng Trường</strong></p>', 1, 1, 0, 1, 1, 'đời sống', NULL, NULL),
(13, 'vi', 'Thanh Hoá lập kỷ lục giá vé V-League', 'Giá vé khu VVIP trên khán đài sân Thanh Hoá có mệnh giá 600.000 đồng, cao nhất trong lịch sử V-League.Giá vé khu VVIP trên khán đài sân Thanh Hoá có mệnh giá 600.000 đồng, cao nhất trong lịch sử V-League.', 'upload/images/LVT-0412-4860-1592386567787.jpg', '2020-06-27', 0, '<p>Ở v&ograve;ng 5 diễn ra thứ Năm 18/6,&nbsp;Thanh Ho&aacute; tiếp Nam Định. V&eacute; ở đa số kh&aacute;n đ&agrave;i vẫn b&igrave;nh thường, thấp nhất l&agrave; 40.000 đồng. Tuy nhi&ecirc;n ở kh&aacute;n đ&agrave;i A, khu VIP v&agrave; VVIP lần lượt c&oacute; mệnh gi&aacute; 400.000 đồng v&agrave; 600.000 đồng.</p>\r\n\r\n<p>&quot;Nhiều người sẽ sốc khi nh&igrave;n v&agrave;o gi&aacute; v&eacute; lần n&agrave;y&quot;, &ocirc;ng bầu Nguyễn Văn Đệ chia sẻ với&nbsp;<em>VnExpress</em>&nbsp;chiều 17/6.&nbsp;&quot;Tuy nhi&ecirc;n, ch&uacute;ng t&ocirc;i&nbsp;chỉ &aacute;p dụng với những khu đặc biệt v&igrave;&nbsp;kh&ocirc;ng ph&aacute;t h&agrave;nh v&eacute; mời miễn ph&iacute; nữa. Ch&uacute;ng t&ocirc;i l&agrave;m như vậy l&agrave; bởi mong muốn c&aacute;c mạnh thường qu&acirc;n g&oacute;p một phần nhỏ chung tay hỗ trợ kinh tế đội b&oacute;ng trong l&uacute;c&nbsp;kh&oacute; khăn v&igrave; Covid-19&quot;.</p>\r\n\r\n<p><img alt=\"Vé trận Thanh Hoá - Nam Định.\" src=\"https://i1-thethao.vnecdn.net/2020/06/17/104772939-947094099074143-6190-4050-2834-1592390728.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=WAjZ3Qi7NJLpmbnxG-ffNw\" /></p>\r\n\r\n<p>V&eacute; trận Thanh Ho&aacute; - Nam Định.</p>\r\n\r\n<p>&Ocirc;ng Đệ cũng cho biết th&ecirc;m rằng, v&eacute; mời miễn ph&iacute; thường được chuyền qua nhiều người&nbsp;dẫn đến việc&nbsp;một số CĐV đến khu vực VIP nhưng ăn mặc l&ocirc;i th&ocirc;i, h&agrave;nh xử khiếm nh&atilde;. Th&ocirc;ng qua h&igrave;nh thức v&eacute; mới, CLB Thanh Ho&aacute; muốn loại bỏ t&igrave;nh trạng tr&ecirc;n.&nbsp;&quot;Ch&uacute;ng t&ocirc;i kh&ocirc;ng &eacute;p người h&acirc;m mộ đi xem với gi&aacute; đắt&quot;, &ocirc;ng n&oacute;i th&ecirc;m. &quot;C&aacute;c CĐV c&oacute; thể chọn chỗ ngồi ph&ugrave; hợp với t&uacute;i tiền của m&igrave;nh. Một số chỗ kh&aacute;n đ&agrave;i A v&agrave; cả kh&aacute;n đ&agrave;i B vẫn c&oacute; mệnh gi&aacute; 40.000 đồng hay 50.000 đồng, kh&ocirc;ng hề đắt đỏ&quot;.</p>\r\n\r\n<p>Thanh Ho&aacute; sở hữu lực lượng CĐV đ&ocirc;ng đảo. Tuy nhi&ecirc;n, th&agrave;nh t&iacute;ch của đội hiện tại kh&ocirc;ng tốt, mới gi&agrave;nh một điểm sau bốn trận. C&aacute;ch đ&acirc;y hai tuần, bầu Đệ đ&atilde; sa thải HLV Fabio Lopez v&agrave; mời HLV Nguyễn Th&agrave;nh C&ocirc;ng thay thế. Trong ng&agrave;y ra mắt của nh&agrave; cầm qu&acirc;n người Nghệ An, Thanh Ho&aacute; ho&agrave; SLNA 0-0.</p>\r\n\r\n<p>Trận đấu với Nam Định ng&agrave;y mai được xem l&agrave; &quot;chung kết ngược&quot; khi cả hai đều đang xếp cuối bảng.</p>\r\n\r\n<p><strong>L&acirc;m Thoả</strong></p>', 6, 7, 0, 1, 1, 'phim', NULL, NULL),
(14, 'vi', 'Vỡ sân ở Hà Tĩnh do \'cán bộ cho người nhà vào\'', 'Ông Nguyễn Văn Hạnh, Phó giám đốc Sở Văn hóa, Thể thao và Du lịch cho rằng lực lượng kiểm soát tự ý mở cửa cho người nhà, khiến sân Hà Tĩnh quá tải trong trận gặp Hà Nội hôm 12/6.', 'upload/images/khan-gia-vao-san-1-4118-1592027710536.jpg', '2020-06-29', 0, '<p>&quot;Nhiều lực lượng l&agrave;m nhiệm vụ như c&ocirc;ng an, qu&acirc;n đội, c&aacute;n bộ quản l&yacute; s&acirc;n... đ&atilde; mở cửa cho người nh&agrave; v&agrave;o với số lượng khoảng 1.000, b&ecirc;n cạnh đ&oacute; l&agrave; nạn v&eacute; giả chen v&agrave;o, khiến ban tổ chức kh&ocirc;ng thể kiểm so&aacute;t nổi&quot;, &ocirc;ng Hạnh cho biết. &quot;Lượng v&eacute; ph&aacute;t ra đảm bảo sức chứa của s&acirc;n. Ph&iacute;a tr&ecirc;n c&aacute;c kh&aacute;n đ&agrave;i c&ograve;n chỗ trống, nhưng nhiều CĐV vẫn tr&agrave;n xuống dưới do thời tiết qu&aacute; nắng n&oacute;ng&quot;</p>\r\n\r\n<p>S&acirc;n vận động H&agrave; Tĩnh, với sức chứa khoảng 16.000 người, gần như kh&ocirc;ng c&ograve;n chỗ trống ở trận H&agrave; Tĩnh &ndash; H&agrave; Nội tại v&ograve;ng 4 V-League 2020 d&ugrave; ban tổ chức chỉ in 12.300 v&eacute; b&aacute;n v&agrave; 1.020 v&eacute; mời. Ph&uacute;t 20, h&agrave;ng ngh&igrave;n người tr&agrave;n xuống đường bi&ecirc;n để đứng, khiến trận đấu tạm dừng khoảng 20 ph&uacute;t.</p>\r\n\r\n<p>Ph&oacute; gi&aacute;m đốc Sở Văn h&oacute;a, Thể thao v&agrave; Du lịch H&agrave; Tĩnh b&agrave;y tỏ sự cố vỡ s&acirc;n l&agrave; bất khả kh&aacute;ng. Ban tổ chức lu&ocirc;n đặt vấn đề an ninh l&ecirc;n h&agrave;ng đầu n&ecirc;n kh&ocirc;ng xảy ra điều g&igrave; nghi&ecirc;m trọng. Trận đấu tới gặp Đ&agrave; Nẵng tại v&ograve;ng 5 tr&ecirc;n s&acirc;n H&agrave; Tĩnh, Sở sẽ giao cho CLB b&aacute;n ra khoảng 10.000 v&eacute;, hy vọng sẽ kh&ocirc;ng qu&aacute; tải. &quot;CLB H&agrave; Nội qu&aacute; nổi tiếng, c&oacute; nhiều tuyển thủ quốc gia như Quang Hải, H&ugrave;ng Dũng, Đức Huy... n&ecirc;n thu h&uacute;t nhiều người h&acirc;m mộ, đặc biệt l&agrave; trẻ em. C&aacute;c trận tiếp theo lượng kh&aacute;n giả tới s&acirc;n sẽ &iacute;t hơn rất nhiều, theo t&iacute;nh to&aacute;n chỉ xấp xỉ khoảng 5.000 người mỗi trận&quot;, &ocirc;ng Hạnh cho hay.</p>\r\n\r\n<p><img alt=\"Một nữ cổ động viên ngất trên khán đài được cảnh sát đưa xuống bên dưới. Ảnh: Đức Hùng\" src=\"https://i1-thethao.vnecdn.net/2020/06/13/vo-san-1-9285-1592027710.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=IFHB7nMdjrXlaEnqXUPxMQ\" /></p>\r\n\r\n<p>Một nữ CĐV ngất tr&ecirc;n kh&aacute;n đ&agrave;i được cảnh s&aacute;t đưa xuống b&ecirc;n dưới. Ảnh:<em>&nbsp;Đức H&ugrave;ng.</em></p>\r\n\r\n<p>Chủ tịch CLB Hồng Lĩnh H&agrave; Tĩnh, &ocirc;ng Nguyễn Tiến Dũng cảm ơn sự quan t&acirc;m của CĐV, v&agrave; b&agrave;y tỏ đ&acirc;y l&agrave; sự cố kh&ocirc;ng may, tr&aacute;ch nhiệm thuộc về ban tổ chức địa phương. &Ocirc;ng n&oacute;i: &quot;Lần đầu tổ chức n&ecirc;n kh&acirc;u kiểm so&aacute;t v&eacute; c&ograve;n nhiều thiếu&nbsp;s&oacute;t. Phe v&eacute; ở H&agrave; Nội, Hải Ph&ograve;ng đổ về, t&igrave;nh trạng v&eacute; giả tr&agrave;n lan. Hệ thống kiểm so&aacute;t v&eacute; nhiều chỗ kh&ocirc;ng x&eacute; v&eacute;, dẫn tới việc v&eacute; bị tuồn trở lại, từ đ&oacute; người ồ ạt v&agrave;o s&acirc;n&quot;.</p>\r\n\r\n<p>Để khắc phục t&igrave;nh trạng s&acirc;n qu&aacute; tải, trận tới CLB H&agrave; Tĩnh sẽ ph&aacute;t h&agrave;nh &iacute;t v&eacute; hơn, siết chặt an ninh, sử dụng m&aacute;y bắn v&eacute; để khắc phục t&igrave;nh trạng b&aacute;n v&eacute; giả. &quot;T&igrave;nh y&ecirc;u b&oacute;ng đ&aacute; của người h&acirc;m mộ H&agrave; Tĩnh thật đ&aacute;ng tự h&agrave;o. Ch&uacute;ng t&ocirc;i sẽ c&oacute; những phương &aacute;n hợp l&yacute; để đ&aacute;p lại tấm ch&acirc;n th&agrave;nh ấy&quot;, &ocirc;ng Dũng th&ocirc;ng tin th&ecirc;m.</p>\r\n\r\n<p><img alt=\"Niềm vui của cổ động viên Hà Tĩnh sau trận hòa Hà Nội. Ảnh: Đức Hùng\" src=\"https://i1-thethao.vnecdn.net/2020/06/13/niem-vui-7667-1592027710.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=_tWq5u3IU3kncMVt3ilvNw\" /></p>\r\n\r\n<p>Niềm vui của CĐV H&agrave; Tĩnh sau trận h&ograve;a H&agrave; Nội. Ảnh:&nbsp;<em>Đức H&ugrave;ng.</em></p>\r\n\r\n<p>Trận 1-1 tr&ecirc;n s&acirc;n kh&aacute;ch gi&uacute;p H&agrave; Nội vươn l&ecirc;n thứ tư&nbsp;với bảy điểm sau bốn v&ograve;ng - k&eacute;m một điểm so với nh&oacute;m ba đội đầu bảng gồm S&agrave;i G&ograve;n, Viettel v&agrave; SLNA. C&ograve;n H&agrave; Tĩnh đạt bốn điểm, đứng thứ ch&iacute;n.</p>\r\n\r\n<p>Video Player is loading.</p>\r\n\r\n<p>Dừng</p>\r\n\r\n<p>Hiện tại&nbsp;0:02</p>\r\n\r\n<p>/</p>\r\n\r\n<p>Thời lượng&nbsp;5:22</p>\r\n\r\n<p>Đ&atilde; tải: 0%</p>\r\n\r\n<p>Tiến tr&igrave;nh: 0%</p>\r\n\r\n<p>Bỏ tắt tiếng</p>\r\n\r\n<p>To&agrave;n m&agrave;n h&igrave;nh</p>\r\n\r\n<ul>\r\n	<li><a href=\"https://vnexpress.net/vo-san-o-tran-ha-tinh-ha-noi-4114847.html\">&#39;Vỡ s&acirc;n&#39; ở trận H&agrave; Tĩnh - H&agrave; Nội</a><a href=\"https://vnexpress.net/vo-san-o-tran-ha-tinh-ha-noi-4114847.html#box_comment_vne\">&nbsp;119</a></li>\r\n	<li><a href=\"https://vnexpress.net/hlv-ha-tinh-can-thong-cam-cho-cdv-o-su-co-vo-san-4114874.html\">HLV H&agrave; Tĩnh: &#39;Cần th&ocirc;ng cảm cho CĐV ở sự cố vỡ s&acirc;n&#39;</a><a href=\"https://vnexpress.net/hlv-ha-tinh-can-thong-cam-cho-cdv-o-su-co-vo-san-4114874.html#box_comment_vne\">&nbsp;74</a></li>\r\n</ul>\r\n\r\n<p><strong>Đức H&ugrave;ng</strong></p>', 2, 6, 0, 1, 1, 'âm nhạc', NULL, NULL),
(15, 'vi', 'Maya Yoshida: \'Bây giờ khó thắng Việt Nam hơn\'', 'Trung vệ đội tuyển Nhật Bản và CLB Sampdoria, Maya Yoshida tin rằng khoảng cách trình độ giữa các đội bóng ở châu Á đang được thu hẹp.\r\n\r\n\"Tại Asian Cup 2019, Nhật Bản đã thắng Việt Nam 1-0\", Yoshida nói với ESPN hôm 12/6. \"Cách đây bảy, tám năm trước, chúng tôi có thể dễ dàng đánh bại họ. Nhưng, hiện tại khó hơn. Một ví dụ khác là Thái Lan. Họ chi rất nhiều tiền để thuê HLV Akira Nishino - người dẫn dắt Nhật Bản ở World Cup 2018\".', 'upload/images/yosshida-1592137997-2092-1592138003682.png', '2020-06-02', 0, '<p>Ở tứ kết Asian Cup 2019, Nhật Bản loại Việt Nam nhờ b&agrave;n thắng duy nhất của Ritsu Doan. Tuy nhi&ecirc;n, cũng ở giải đấu n&agrave;y năm 2007,&nbsp;Nhật Bản đ&egrave; bẹp chủ nh&agrave; Việt Nam 4-1 trong trận đấu ở Mỹ Đ&igrave;nh, sau đ&oacute; cả hai dắt tay nhau v&agrave;o tứ kết.</p>\r\n\r\n<p>Nhật Bản chỉ về nh&igrave; Asian Cup 2019, khi thua Qatar 1-3 ở chung kết. Tại World Cup 2018, họ thua ngược Bỉ ở v&ograve;ng 1/8. Năm 2011, họ từng đứng thứ 19 FIFA, nhưng b&acirc;y giờ rơi xuống thứ 28. Việt Nam th&igrave; đang đạt phong độ tốt, với chức v&ocirc; địch AFF Cup 2018 v&agrave; dẫn đầu bảng G v&ograve;ng loại World Cup 2020 khu vực ch&acirc;u &Aacute;. Thầy tr&ograve; Park Hang-seo hiện đứng thứ 94 FIFA, chỉ k&eacute;m kỷ lục th&aacute;ng 9/1998 (thứ 84).</p>\r\n\r\n<p>Yoshida l&agrave; một trong những cầu thủ Nhật Bản th&agrave;nh danh ở ch&acirc;u &Acirc;u. Anh từng thi đấu bảy năm rưỡi cho Southampton (từ 2012), trước khi sang Sampdoria theo dạng cho mượn.&nbsp;</p>\r\n\r\n<p>Khi Southampton kết th&uacute;c thứ s&aacute;u ở Ngoại hạng Anh&nbsp;m&ugrave;a&nbsp;2015-2016, v&agrave; v&agrave;o chung kết Cup Li&ecirc;n đo&agrave;n Anh một năm sau đ&oacute;,&nbsp;Yoshida ch&iacute;nh l&agrave; một trong hai trụ cột ở trung t&acirc;m h&agrave;ng thủ. Người c&ograve;n lại l&agrave;&nbsp;Virgil van Dijk. Trước đ&oacute;, hai người từng thi đấu chung ở giải v&ocirc; địch H&agrave; Lan, nơi Yoshida đ&aacute; cho CLB ch&acirc;u &Acirc;u đầu ti&ecirc;n của anh -&nbsp;VVV-Venlo, c&ograve;n Van Dijk kho&aacute;c &aacute;o Groningen.</p>\r\n\r\n<p>&quot;Hồi đ&oacute; cậu ấy đ&atilde; cao lớn v&agrave; đạt thể lực dồi d&agrave;o, nhưng kỹ thuật th&igrave; chưa ho&agrave;n thiện v&agrave; thỉnh thoảng c&ograve;n mất tập trung&quot;,&nbsp;Yoshida kể lại. &quot;Khi t&ocirc;i gặp lại Van Dijk ở Southampton, cậu ấy đ&atilde; trở th&agrave;nh một cầu thủ to&agrave;n diện hơn. Ai cũng c&oacute; thể nhận ra cậu ấy sẽ trở th&agrave;nh một trong những hậu vệ hay nhất của Ngoại hạng Anh v&agrave; sẽ chuyển đến một CLB n&agrave;o đ&oacute; lớn hơn&quot;.&nbsp;</p>\r\n\r\n<p>Yoshida kể th&ecirc;m: &quot;Đ&ocirc;i khi t&ocirc;i cảm thấy ghen tị với cậu ấy. Sau mỗi buổi tập t&ocirc;i đều phải tập gym, v&agrave; qua cửa sổ hướng ra b&atilde;i đỗ xe, t&ocirc;i đều thấy cậu ấy l&agrave; một trong những người về nh&agrave; sớm nhất. Cậu ấy kh&ocirc;ng cần tập gym hay tập phục hồi. Cậu ấy&nbsp;c&oacute; sẵn tố chất thể lực, tốc độ hơn người. Kỹ thuật của cậu ấy cũng tốt v&agrave; bản năng thủ lĩnh nữa&quot;.&nbsp;</p>\r\n\r\n<p><strong>Ho&agrave;ng An</strong>&nbsp;(theo&nbsp;<em>ESPN</em>)</p>', 5, 21, 0, 1, 1, 'sản phẩm', NULL, NULL),
(16, 'vi', 'Ủy ban chứng khoán có thể \'dập cầu dao\' nếu thị trường biến động', 'Bộ Tài chính đang đề xuất sửa Nghị định để Uỷ ban Chứng khoán Nhà nước có thể ngắt mạch giao dịch khi thị trường quá biến động.\r\n\r\nDự thảo Nghị định thi hành Luật Chứng khoán vừa được Bộ Tài chính lấy ý kiến dành 5 điều khoản đề cập các biện pháp giám sát an ninh và bảo đảm an toàn thị trường.', 'upload/images/chungkhoan-18-4679-1592365839658.jpg', '2020-06-12', 0, '<p>Việc biến động đ&aacute;ng kể về tổng gi&aacute; trị vốn ho&aacute;, gi&aacute; trị giao dịch mỗi phi&ecirc;n, thanh khoản của khối ngoại... hoặc c&oacute; sự kiện ảnh hưởng ti&ecirc;u cực đến vận h&agrave;nh th&ocirc;ng suốt được xem l&agrave; t&igrave;nh huống c&oacute; nguy cơ đe doạ an to&agrave;n thị trường. B&ecirc;n cạnh đ&oacute;, một tổ chức kinh doanh chứng kho&aacute;n quy m&ocirc; lớn hoặc nhiều tổ chức kh&aacute;c ngừng hoạt động, giải thể, ph&aacute; sản cũng được nhắc đến trong danh s&aacute;ch rủi ro.</p>\r\n\r\n<p>Trong c&aacute;c trường hợp n&agrave;y, Ủy ban chứng kho&aacute;n Nh&agrave; nước (SSC) được thực hiện nhiều biện ph&aacute;p điều tiết nhằm đảm bảo t&iacute;nh ổn định v&agrave; to&agrave;n vẹn của thị trường. Cơ quan n&agrave;y c&oacute; thể y&ecirc;u cầu Sở giao dịch chứng kho&aacute;n thay đổi giờ mở cửa, thu hẹp bi&ecirc;n độ dao động gi&aacute;, ngắt mạch thị trường đối với c&aacute;c giao dịch khớp lệnh li&ecirc;n tục. Đồng thời, c&oacute; thể tạm ngừng, đ&igrave;nh chỉ một phần hoặc kh&ocirc;i phục to&agrave;n bộ hoạt động giao dịch tr&ecirc;n thị trường</p>\r\n\r\n<p>Sở giao dịch chứng kho&aacute;n phải b&aacute;o c&aacute;o SSC v&agrave; c&ocirc;ng bố th&ocirc;ng tin trong&nbsp;24 giờ từ khi &aacute;p dụng c&aacute;c biện ph&aacute;p n&agrave;y. Thời hạn thực hiện mỗi lần kh&ocirc;ng qu&aacute; 5 ng&agrave;y, nhưng trong trường hợp cần thiết gia hạn&nbsp;phải b&aacute;o c&aacute;o Bộ trưởng&nbsp;T&agrave;i ch&iacute;nh.</p>\r\n\r\n<p><img alt=\"Nhà đầu tư theo dõi bảng giá khi thị trường giảm điểm ngày 16/3. Ảnh: Quỳnh Trần.\" src=\"https://i1-kinhdoanh.vnecdn.net/2020/06/17/chungkhoan-18-4679-1592365839.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=89yolRv1w3W-9vneij7uJQ\" /></p>\r\n\r\n<p>Nh&agrave; đầu tư theo d&otilde;i bảng gi&aacute; khi thị trường giảm điểm ng&agrave;y 16/3.&nbsp;<em>Ảnh: Quỳnh Trần.</em></p>\r\n\r\n<p>Một số nước sử dụng hệ thống tự động ngắt mạch giao dịch khi gi&aacute; chứng kho&aacute;n giảm qu&aacute; ngưỡng cho ph&eacute;p. Tại Mỹ, khi mức giảm của c&aacute;c chỉ số chạm 7%, hệ thống sẽ k&iacute;ch&nbsp;hoạt cơ chế ngắt mạch cấp độ 1.&nbsp;Hai cấp độ kế tiếp được thiết lập ở mốc 13% v&agrave; 20%. Ở cấp độ 2, thời gian dừng giao dịch vẫn l&agrave; 15 ph&uacute;t. Nhưng nếu chạm ngưỡng cấp độ 3, chứng kho&aacute;n Mỹ sẽ dừng cả phi&ecirc;n giao dịch.</p>\r\n\r\n<p>Trong khi đ&oacute; tại H&agrave;n Quốc, thị trường sẽ tạm ngưng trong 20 ph&uacute;t nếu chỉ số Kospi hoặc Kosdaq giảm hơn 20% so với gi&aacute; đ&oacute;ng cửa phi&ecirc;n trước đ&oacute;. C&ocirc;ng cụ n&agrave;y chỉ c&oacute; thể được k&iacute;ch hoạt tối đa một lần mỗi ng&agrave;y.</p>\r\n\r\n<p>N&oacute;i với&nbsp;<em>VnExpress</em>&nbsp;sau phi&ecirc;n giao dịch ng&agrave;y 3/2 - phi&ecirc;n m&agrave; c&oacute; l&uacute;c&nbsp;VN-Index giảm hơn 40 điểm, &ocirc;ng Trần Văn Dũng &ndash; Chủ tịch SSC cho biết Việt Nam chưa c&oacute; ch&iacute;nh s&aacute;ch n&agrave;y n&ecirc;n việc tạm ngưng giao dịch sẽ theo quyết định của nh&agrave; điều h&agrave;nh -&nbsp;Bộ T&agrave;i ch&iacute;nh.&nbsp;</p>\r\n\r\n<p><strong>Phương Đ&ocirc;ng</strong></p>', 6, 7, 0, 1, 1, 'phim', NULL, NULL),
(17, 'vi', 'Thanh khoản sàn HoSE xấp xỉ 1 tỷ USD', 'Nhiều lệnh mua thoả thuận tại cổ phiếu Vinhomes trong những phút cuối phiên đẩy thanh khoản nhảy vọt từ 7.000 tỷ lên 22.700 tỷ đồng.', 'upload/images/Screen-Shot-2020-06-15-at-3-13-2269-8475-159220887623.png', '2020-06-08', 0, '<ul>\r\n	<li>15h00\r\n	<h2>Thanh khoản kỷ lục</h2>\r\n\r\n	<p>Thanh khoản tr&ecirc;n s&agrave;n TP HCM trước phi&ecirc;n ATC chỉ hơn 7.000 tỷ đồng, nhưng trong &iacute;t ph&uacute;t cuối phi&ecirc;n đ&atilde; nhảy vọt l&ecirc;n 22.709 tỷ đồng khi xuất hiện c&aacute;c lệnh mua thoả thuận của nh&agrave; đầu tư nước ngo&agrave;i tại VHM với tổng gi&aacute; trị 15.100 tỷ đồng. Tổng khối lượng được sang tay vượt 708 triệu đơn vị.</p>\r\n\r\n	<p><strong>Đ&acirc;y l&agrave; phi&ecirc;n c&oacute; khối lượng giao dịch cao nhất trong lịch sử gần 20 năm của s&agrave;n chứng kho&aacute;n TP HCM v&agrave; c&oacute; gi&aacute; trị thanh khoản xếp thứ hai.</strong>&nbsp;Trước đ&oacute;, phi&ecirc;n 18/5/2018, nh&agrave; đầu tư nước ngo&agrave;i cũng mua v&agrave;o một lượng lớn cổ phiếu VHM v&agrave; đẩy thanh khoản to&agrave;n thị trường l&ecirc;n mức kỷ lục l&agrave; 34.920 tỷ đồng.&nbsp;</p>\r\n	<img alt=\"Screen-Shot-2020-06-15-at-3-13-2269-8475\" src=\"https://i1-kinhdoanh.vnecdn.net/2020/06/15/Screen-Shot-2020-06-15-at-3-13-2269-8475-1592208876.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=5nboBA1XPAGm7sntb9NsyA\" />\r\n	<p>&nbsp;</p>\r\n\r\n	<p>Đ&agrave; giảm của VN-Index tiếp tục k&eacute;o d&agrave;i trong những ph&uacute;t cuối phi&ecirc;n khi t&acirc;m l&yacute; nh&agrave; đầu tư th&ecirc;m bi quan. Chỉ số đại diện cho s&agrave;n TP HCM mất hơn 31 điểm, tương đương 3,6% v&agrave; chốt phi&ecirc;n tại 832,47 điểm. B&ecirc;n cầm cổ phiếu chiếm ưu thế ho&agrave;n to&agrave;n với 291 m&atilde; giảm, gấp hơn ba lần số m&atilde; tăng.</p>\r\n\r\n	<p>Rổ VN30 biến động mạnh trong những ph&uacute;t cuối khi nhiều cổ phiếu đồng loạt giảm hết bi&ecirc;n độ hoặc tiệm cận mức n&agrave;y, điển h&igrave;nh như CTD, SSI, VHM v&agrave; VPB. C&aacute;c cổ phiếu kh&aacute;c cũng nới rộng bi&ecirc;n độ giảm l&ecirc;n mức phổ biến tr&ecirc;n 3% so với tham chiếu.</p>\r\n\r\n	<p>Cổ phiếu của STB vẫn ngược d&ograve;ng thị trường, dẫn đầu danh s&aacute;ch cổ phiếu bluechip tăng điểm khi thị gi&aacute; t&iacute;ch luỹ th&ecirc;m 2,1% l&ecirc;n 12.050 đồng. Trong số 5 cổ phiếu duy tr&igrave; được sắc xanh th&igrave; nh&oacute;m ng&acirc;n h&agrave;ng đ&oacute;ng g&oacute;p 3 m&atilde;, ngo&agrave;i Sacombank c&ograve;n c&oacute; HDBank v&agrave; Eximbank.</p>\r\n\r\n	<p>Chia sẻ:<a href=\"javascript:void(0);\">facebook</a><a href=\"javascript:void(0);\">twitter</a></p>\r\n	</li>\r\n	<li>14h45\r\n	<h2>Khối ngoại thỏa thuận&nbsp;15.000 tỷ đồng cổ phiếu VHM&nbsp;</h2>\r\n\r\n	<p>3 ph&uacute;t trước khi kết th&uacute;c phi&ecirc;n ATC, một loạt giao dịch&nbsp;thỏa thuận khối lượng lớn m&atilde; VHM được thực hiện. Từ 14h42 đến 14h44, hơn 201 triệu cổ phiếu VHM được khối ngoại thỏa thuận từ cổ đ&ocirc;ng nội tại gi&aacute;&nbsp;tham chiếu 75.000 đồng. Tổng&nbsp;gi&aacute; trị giao dịch n&agrave;y đạt hơn 15.000 tỷ đồng.&nbsp;</p>\r\n\r\n	<p>Tuy nhi&ecirc;n, tr&ecirc;n thị trường khớp lệnh, VHM chốt phi&ecirc;n giảm 6,7%, đứng ở mức 70.000 đồng. Thanh khoản k&ecirc;nh khớp lệnh hơn 3,3 triệu cổ phiếu.</p>\r\n\r\n	<p>Chia sẻ:<a href=\"javascript:void(0);\">facebook</a><a href=\"javascript:void(0);\">twitter</a></p>\r\n	</li>\r\n	<li>14h20\r\n	<h2>&Aacute;p lực chốt lời mạnh</h2>\r\n\r\n	<p>Lực b&aacute;n lớn khiến&nbsp;tr&ecirc;n 305 cổ phiếu giảm điểm. Nhiều nh&oacute;m cổ phiếu đ&atilde; xuất hiện t&igrave;nh trạng b&aacute;n s&agrave;n, điển h&igrave;nh như SSI v&agrave; HCM của nh&oacute;m chứng kho&aacute;n, MSH của nh&oacute;m dệt may.</p>\r\n\r\n	<p>Rổ vốn ho&aacute; lớn đang gh&igrave; thị trường chung xuống s&acirc;u. 10 cổ phiếu t&aacute;c động ti&ecirc;u cực nhất đang lấy của VN-Index hơn 11 điểm v&agrave; đều l&agrave; trụ cột trong những phi&ecirc;n trước như VHM, VCB, BID.</p>\r\n\r\n	<p>Sắc xanh chỉ le l&oacute;i ở một số cổ phiếu c&oacute; mạch tăng đột biến gần đ&acirc;y v&agrave; được hỗ trợ bởi nhiều th&ocirc;ng tin t&iacute;ch cực như ITA, HQC, HBC... nhưng bi&ecirc;n độ cũng thu hẹp đ&aacute;ng kể.</p>\r\n\r\n	<p>VN-Index hiện mất hơn 28 điểm, xuống 835 điểm.</p>\r\n\r\n	<p>Trước phi&ecirc;n ATC, khoảng 475 triệu cổ phiếu đ&atilde; được sang tay với gi&aacute; trị hơn 7.000 tỷ đồng. Khối ngoại cũng nhanh ch&oacute;ng xả h&agrave;ng khi gi&aacute; trị b&aacute;n đ&atilde; vượt 400 tỷ đồng, tập trung v&agrave;o c&aacute;c cổ phiếu vốn ho&aacute; lớn như VNM, VIC v&agrave; MSN.</p>\r\n\r\n	<p>Chia sẻ:<a href=\"javascript:void(0);\">facebook</a><a href=\"javascript:void(0);\">twitter</a></p>\r\n	</li>\r\n	<li>13h30\r\n	<h2>VN-Index mất hơn 23 điểm</h2>\r\n\r\n	<p>Chỉ số đại diện cho s&agrave;n TP HCM rơi thẳng đứng v&agrave;o đầu phi&ecirc;n chiều, c&oacute; l&uacute;c mất hơn 23 điểm v&agrave; xuống s&aacute;t v&ugrave;ng 840 điểm. Nhu cầu&nbsp;chốt lời quyết liệt l&agrave; nguy&ecirc;n nh&acirc;n ch&iacute;nh của việc chỉ số lao dốc mạnh.&nbsp;</p>\r\n\r\n	<p>Sắc đỏ bao tr&ugrave;m thị trường với gần 300 cổ phiếu giao dịch dưới tham chiếu, trong đ&oacute; rổ VN30 đ&oacute;ng g&oacute;p 25 m&atilde;. Bi&ecirc;n độ giảm của rổ n&agrave;y kh&aacute; lớn, thấp nhất l&agrave; VIC mất 1,2% v&agrave; nhiều nhất l&agrave; MSN mất gần 5%. Thanh khoản rổ n&agrave;y đạt tr&ecirc;n 108 triệu cổ phiếu, trị gi&aacute; hơn 2.300 tỷ đồng. 10 cổ phiếu t&aacute;c động ti&ecirc;u cực nhất đến thị trường phần lớn thuộc nh&oacute;m ng&acirc;n h&agrave;ng v&agrave; h&agrave;ng kh&ocirc;ng, như VCB, VJC, BID, TCB, HVN.</p>\r\n\r\n	<p>Khối ngoại giữ trạng th&aacute;i mua b&aacute;n thận trọng. Gi&aacute; trị mua v&agrave;o mới khoảng 16 tỷ đồng, trong khi b&aacute;n ra xấp xỉ 36 tỷ đồng. &nbsp;</p>\r\n\r\n	<p>Chia sẻ:<a href=\"javascript:void(0);\">facebook</a><a href=\"javascript:void(0);\">twitter</a></p>\r\n	</li>\r\n	<li>11h30\r\n	<h2>VN-Index chốt phi&ecirc;n s&aacute;ng giảm gần 0,6%</h2>\r\n	<img alt=\"A-nh-chu-p-Ma-n-hi-nh-2020-06-4671-3851-\" src=\"https://i1-kinhdoanh.vnecdn.net/2020/06/15/A-nh-chu-p-Ma-n-hi-nh-2020-06-4671-3851-1592197566.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=FT-Kq26ycKrwaBrWpJ2cbQ\" />\r\n	<p>VN-Index chốt phi&ecirc;n s&aacute;ng ng&agrave;y 15/6 dưới tham chiếu. Ảnh:&nbsp;<em>VNDirect.</em></p>\r\n\r\n	<p>Thị trường giữ nhịp giằng co đến trước giờ nghỉ trưa. Chốt phi&ecirc;n s&aacute;ng, VN-Index giảm 0,58% xuống 858,53 điểm. VN30-Index giảm 0,67% xuống 802,38 điểm. Tr&ecirc;n s&agrave;n H&agrave; Nội, HNX-Index giảm 0,38%, trong khi UPCOM-Index vượt nhẹ tr&ecirc;n tham chiếu.</p>\r\n\r\n	<p>Sắc đỏ vẫn chiếm &aacute;p đảo với hơn 200 m&atilde; giảm v&agrave;o cuối phi&ecirc;n s&aacute;ng, 56 m&atilde; đứng tham chiếu v&agrave; 151 m&atilde; tăng. Trong nh&oacute;m VN30, 20/30 m&atilde; bluechip giảm gi&aacute;. Giao dịch giằng co khiến Thanh khoản ở mức thấp,&nbsp;chỉ đạt gần 3.900 tỷ đồng tr&ecirc;n hai s&agrave;n ni&ecirc;m yết.</p>\r\n\r\n	<p>Chia sẻ:<a href=\"javascript:void(0);\">facebook</a><a href=\"javascript:void(0);\">twitter</a></p>\r\n	</li>\r\n	<li>\r\n	<p>&nbsp;</p>\r\n	</li>\r\n	<li>11h05\r\n	<h2>Cổ phiếu HBC tăng trần</h2>\r\n\r\n	<p>Sau nhịp giảm cuối tuần trước, m&atilde; HBC của Tập đo&agrave;n X&acirc;y dựng H&ograve;a B&igrave;nh đ&atilde; tăng trần trở lại trong phi&ecirc;n s&aacute;ng. Thị gi&aacute; của cổ phiếu n&agrave;y đ&atilde; tăng hơn 45% sau chuỗi phi&ecirc;n tăng li&ecirc;n tục, từ mức 8.760 đồng l&ecirc;n 12.750 đồng.</p>\r\n\r\n	<p>&nbsp;</p>\r\n	<a href=\"https://vnexpress.net/con-trai-ong-le-viet-hai-ung-cu-vao-ban-lanh-dao-hoa-binh-4115632.html\"><img alt=\"Con trai ông Lê Viết Hải ứng cử vào ban lãnh đạo Hoà Bình\" src=\"https://i1-kinhdoanh.vnecdn.net/2020/06/15/leviethieu-1592188288-9076-1592188294.jpg?w=180&amp;h=108&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=RKQ1lAZg6iHt66Uvlsrfng\" /></a>\r\n\r\n	<p><a href=\"https://vnexpress.net/con-trai-ong-le-viet-hai-ung-cu-vao-ban-lanh-dao-hoa-binh-4115632.html\">Con trai &ocirc;ng L&ecirc; Viết Hải ứng cử v&agrave;o ban l&atilde;nh đạo Ho&agrave; B&igrave;nh</a></p>\r\n\r\n	<p>L&ecirc; Viết Hiếu, sinh năm 1992, vừa&nbsp;được&nbsp;l&atilde;nh đạo Tập đo&agrave;n X&acirc;y dựng Ho&agrave; B&igrave;nh đề cử v&agrave;o Hội đồng quản trị nhiệm kỳ 2019-2024 thay một nh&acirc;n sự vừa từ nhiệm.</p>\r\n\r\n	<p>Chia sẻ:<a href=\"javascript:void(0);\">facebook</a><a href=\"javascript:void(0);\">twitter</a></p>\r\n	</li>\r\n	<li>11h00\r\n	<h2>Giằng co gần tham chiếu</h2>\r\n\r\n	<p>VN-Index trở lại ngưỡng 860 điểm v&agrave;o cuối phi&ecirc;n s&aacute;ng, thu hẹp đ&agrave; giảm c&ograve;n 0,3%. Lực cung hạ nhiệt khi c&aacute;c cổ phiếu tiến v&agrave;o v&ugrave;ng gi&aacute; đỏ, trong khi lực cầu tham gia bắt đ&aacute;y mạnh hơn gi&uacute;p thị trường trở lại c&acirc;n bằng. Số m&atilde; tăng v&agrave; giảm tr&ecirc;n HoSE cũng được thu hẹp, giữ tỷ lệ 16:19. Trong nh&oacute;m VN30, 18/30 m&atilde; đang thấp hơn tham chiếu.</p>\r\n\r\n	<p>Chia sẻ:<a href=\"javascript:void(0);\">facebook</a><a href=\"javascript:void(0);\">twitter</a></p>\r\n	</li>\r\n	<li>10h30\r\n	<h2>Nhiều cổ phiếu penny vẫn tăng trần</h2>\r\n\r\n	<p>Trong khi trạng th&aacute;i chung ph&acirc;n h&oacute;a, giằng co gần tham chiếu, nhiều m&atilde; thị gi&aacute; dưới 10.000 đồng vẫn duy tr&igrave; sắc t&iacute;m. Đến giữa phi&ecirc;n s&aacute;ng, FIT, EVG, SJF, HCD, PXT, TSC, HQC trong trạng th&aacute;i trắng bảng b&ecirc;n b&aacute;n.&nbsp;</p>\r\n\r\n	<p>T&iacute;nh cả phi&ecirc;n s&aacute;ng nay, m&atilde; HQC đ&atilde; tăng trần phi&ecirc;n thứ 10 li&ecirc;n tiếp. Thị gi&aacute; cổ phiếu n&agrave;y tăng từ 1.180 đồng l&ecirc;n 2.120 đồng. Thanh khoản c&oacute; những phi&ecirc;n l&ecirc;n tới hơn 80 triệu cổ phiếu được sang tay.</p>\r\n\r\n	<p>Chia sẻ:<a href=\"javascript:void(0);\">facebook</a><a href=\"javascript:void(0);\">twitter</a></p>\r\n	</li>\r\n	<li>10h20\r\n	<h2>Khối ngoại b&aacute;n r&ograve;ng hơn 40 tỷ đồng</h2>\r\n\r\n	<p>Sau nhịp mua r&ograve;ng v&agrave;o tuần trước, khối ngoại quay lại b&aacute;n r&ograve;ng trong phi&ecirc;n đầu tuần với gi&aacute; trị hơn 40 tỷ đồng tr&ecirc;n HoSE. Lực b&aacute;n tập trung v&agrave;o một số m&atilde; bluechip như VNM, VCB, HDB, VIC. Trong khi đ&oacute;, lực mua chứng chỉ quỹ ETF VFMVN DIAMOND chỉ hơn 1 tỷ đồng.</p>\r\n\r\n	<p>Chia sẻ:<a href=\"javascript:void(0);\">facebook</a><a href=\"javascript:void(0);\">twitter</a></p>\r\n	</li>\r\n	<li>9h50\r\n	<h2>Sắc đỏ chiếm ưu thế</h2>\r\n	<img alt=\"VN-Index giảm đầu phiên ngày 16/6. Ảnh: VNDirect.\" src=\"https://i1-kinhdoanh.vnecdn.net/2020/06/15/A-nh-chu-p-Ma-n-hi-nh-2020-06-8017-8009-1592190025.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=omPhJBYJXFB6k1GTVXI0gQ\" />\r\n	<p>VN-Index giảm đầu phi&ecirc;n ng&agrave;y 15/6. Ảnh:&nbsp;<em>VNDirect.</em></p>\r\n\r\n	<p>Thị trường bật tăng ngay sau phi&ecirc;n ATO nhưng xu hướng t&iacute;ch cực kh&ocirc;ng duy tr&igrave; được l&acirc;u. Nhịp k&eacute;o l&ecirc;n đầu giờ k&iacute;ch hoạt l&agrave;n s&oacute;ng b&aacute;n ra mạnh, trong khi lực cầu thận trọng khiến c&aacute;c chỉ số đều giảm. VN-Index nhanh ch&oacute;ng l&ugrave;i về dưới tham chiếu v&agrave; nới rộng đ&agrave; giảm khi b&ecirc;n b&aacute;n nắm quyền chủ động. Đến 9h50, VN-Index giảm gần 1% l&ugrave;i về ngưỡng 855 điểm. VN30-Index giảm về dưới 800 điểm. Tr&ecirc;n s&agrave;n H&agrave; Nội, HNX-Index v&agrave; UPCOM-Index c&ugrave;ng ch&igrave;m trong sắc đỏ.</p>\r\n\r\n	<p>Thanh khoản trong đầu phi&ecirc;n s&aacute;ng đạt hơn 1.700 tỷ đồng. S&agrave;n HoSE ghi nhận gần 200 m&atilde; giảm, 68 m&atilde; đứng tham chiếu v&agrave; 105 m&atilde; tăng. Ri&ecirc;ng trong nh&oacute;m VN30, b&ecirc;n b&aacute;n nắm quyền chủ động với 24/30 m&atilde; bluechip về dưới tham chiếu.</p>\r\n\r\n	<p>Trong nh&oacute;m vốn h&oacute;a lớn, STB v&agrave; HDB l&agrave; hai m&atilde; t&iacute;ch cực nhất với bi&ecirc;n độ tăng lần lượt l&agrave; 5,1% v&agrave; 3,7%. Theo sau l&agrave; POW, VRE tăng hơn 1%. Ở chiều ngược lại, BID v&agrave; VJC giảm hơn 2%, SBT, PNJ giảm 1,8%, MWG giảm 1,4%.</p>\r\n\r\n	<p>Chia sẻ:<a href=\"javascript:void(0);\">facebook</a><a href=\"javascript:void(0);\">twitter</a></p>\r\n	</li>\r\n	<li>\r\n	<p>&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p>&nbsp;</p>\r\n	<a href=\"https://vnexpress.net/chung-khoan-tuan-nay-kho-doan-4115238.html\"><img alt=\"Chứng khoán tuần này khó đoán\" src=\"https://i1-kinhdoanh.vnecdn.net/2020/06/14/chungkhoan40-1592094431-9893-1592094621.jpg?w=180&amp;h=108&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Rc7L-Z0fsv8cseqezckvFQ\" /></a>\r\n\r\n	<p><a href=\"https://vnexpress.net/chung-khoan-tuan-nay-kho-doan-4115238.html\">Chứng kho&aacute;n tuần n&agrave;y kh&oacute; đo&aacute;n</a></p>\r\n\r\n	<p>Trong kịch bản t&iacute;ch cực, VN-Index c&oacute; thể trở lại ngưỡng 880-890 điểm, nếu kh&ocirc;ng, thị trường c&oacute; thể l&ugrave;i về v&ugrave;ng 840&nbsp;điểm, thậm ch&iacute; xuống gần 800 điểm.</p>\r\n\r\n	<p>Chia sẻ:<a href=\"javascript:void(0);\">facebook</a><a href=\"javascript:void(0);\">twitter</a></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Minh Sơn - Phương Đ&ocirc;ng</strong></p>', 6, 7, 0, 1, 1, 'âm nhạc', NULL, NULL);
INSERT INTO `tin` (`idTin`, `lang`, `TieuDe`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`, `tags`, `created_at`, `updated_at`) VALUES
(18, 'vi', 'Vì sao Triều Tiên \'rung chuông báo tử\' quan hệ với Hàn Quốc?', 'Trong hơn một năm qua, Bình Nhưỡng cảnh báo Hàn Quốc ngày càng gay gắt rằng vai trò trung gian của họ trong quan hệ Mỹ - Triều đang không đi đến đâu.\r\n\r\nNgày 16/6, Triều Tiên giật sập văn phòng liên lạc chung với Hàn Quốc ở Kaesong. Văn phòng này đóng vai trò như đại sứ quán hai nước, đồng thời cung cấp kênh liên lạc trực tiếp giữa hai miền bán đảo Triều Tiên. Hàng chục quan chức hai nước làm việc trong tòa nhà này và quan chức Hàn Quốc tới làm việc tại đây mỗi tuần.', 'upload/images/im-199235-1-3405-1592374714735.jpg', '2020-06-12', 0, '<p>Vụ giật sập đ&atilde; ph&aacute; hủy hai năm giảm căng thẳng tr&ecirc;n b&aacute;n đảo Triều Ti&ecirc;n, vốn được kỳ vọng sẽ dẫn đến phi hạt nh&acirc;n h&oacute;a Triều Ti&ecirc;n hay k&yacute; kết hiệp ước h&ograve;a b&igrave;nh ch&iacute;nh thức chấm dứt Chiến tranh li&ecirc;n Triều 1950 - 1953. Kim Jong-un đang h&agrave;nh động đ&uacute;ng như lời cảnh b&aacute;o &ocirc;ng đ&atilde; lặp đi lặp lại rằng &ocirc;ng sẽ đưa mối quan hệ li&ecirc;n Triều sang giai đoạn mới, coi H&agrave;n Quốc kh&ocirc;ng phải l&agrave; một đối t&aacute;c h&ograve;a giải m&agrave; l&agrave; &quot;kẻ th&ugrave;&quot;.</p>\r\n\r\n<p>H&agrave;n Quốc n&oacute;i rằng Triều Ti&ecirc;n sẽ phải chịu tr&aacute;ch nhiệm cho h&agrave;nh động của m&igrave;nh v&agrave; cảnh b&aacute;o nếu B&igrave;nh Nhưỡng tiếp tục l&agrave;m t&igrave;nh h&igrave;nh th&ecirc;m trầm trọng, họ sẽ c&oacute; biện ph&aacute;p đ&aacute;p trả tương xứng.</p>\r\n\r\n<p>Tuần trước, Triều Ti&ecirc;n cho biết họ đ&atilde; b&aacute;c bỏ đề xuất gần đ&acirc;y của Tổng thống H&agrave;n Quốc Moon Jae-in về việc cử đặc ph&aacute;i vi&ecirc;n tới B&igrave;nh Nhưỡng. Qu&acirc;n đội Triều Ti&ecirc;n cho biết họ đang xin &ocirc;ng Kim chấp thuận &quot;<a href=\"https://vnexpress.net/trieu-tien-se-dua-quan-quay-lai-bien-gioi-voi-han-quoc-4116708.html\">nối lại</a>&nbsp;tất cả cuộc tập trận&nbsp;thường xuy&ecirc;n&quot; gần bi&ecirc;n giới biển ph&iacute;a t&acirc;y đang tranh chấp, mặc d&ugrave; đ&atilde; c&oacute; thỏa thuận li&ecirc;n Triều cấm c&aacute;c cuộc tập trận như vậy.</p>\r\n\r\n<p>Những diễn biến&nbsp;giữa B&igrave;nh Nhưỡng v&agrave; Seoul b&aacute;o hiệu rằng v&ograve;ng xo&aacute;y khủng hoảng quan hệ li&ecirc;n Triều &quot;kh&ocirc;ng thể cứu v&atilde;n&quot;, Lee Byong-chul, chuy&ecirc;n gia về Triều Ti&ecirc;n tại Viện Nghi&ecirc;n cứu Viễn Đ&ocirc;ng Kyungnam tại Seoul, cho biết.</p>\r\n\r\n<p><strong>&quot;Triều Ti&ecirc;n đ&atilde; rung chu&ocirc;ng b&aacute;o tử cho mối quan hệ với ch&iacute;nh quyền Moon Jae-in&quot;, &ocirc;ng n&oacute;i.</strong></p>\r\n\r\n<p>Tam gi&aacute;c quan hệ Kim - Moon - Trump đ&atilde; c&oacute; nhiều biến động trong ba năm qua. Trump - Kim từng d&agrave;nh phần lớn năm 2017 để đấu khẩu, khi Trump cảnh b&aacute;o tr&uacute;t &quot;<a href=\"https://vnexpress.net/lua-va-thinh-no-my-trieu-co-the-bung-phat-nam-2020-4033042.html\">lửa v&agrave; thịnh nộ</a>&quot; l&ecirc;n Triều Ti&ecirc;n sau c&aacute;c vụ thử t&ecirc;n lửa đạn đạo tầm xa v&agrave; hạt nh&acirc;n của B&igrave;nh Nhưỡng. Trong khi đ&oacute;, Triều Ti&ecirc;n gọi Trump l&agrave; &quot;g&atilde; lẩm cẩm loạn tr&iacute;&quot;. Nỗ lực l&agrave;m trung gian h&ograve;a giải của Moon Jae-in đạt được th&agrave;nh quả khi Trump - Kim gặp nhau tại Singapore v&agrave;o năm 2018, đ&aacute;nh dấu cuộc gặp thượng đỉnh đầu ti&ecirc;n giữa hai quốc gia.</p>\r\n\r\n<p>Nhưng mối quan hệ giữa hai l&atilde;nh đạo&nbsp;trở n&ecirc;n tồi tệ khi cả hai ra về tay trắng sau cuộc gặp thượng đỉnh lần hai th&aacute;ng 2/2019. &Ocirc;ng Kim kh&ocirc;ng thể thuyết phục Mỹ nới lỏng lệnh trừng phạt m&agrave; &ocirc;ng rất cần để x&acirc;y dựng lại nền kinh tế. Sau thất bại đ&oacute;, Triều Ti&ecirc;n tr&uacute;t sự thất vọng l&ecirc;n người trung gian - Moon Jae-in.</p>\r\n\r\n<p>Trong những th&aacute;ng gần đ&acirc;y, Triều Ti&ecirc;n gửi nhiều cảnh b&aacute;o tới H&agrave;n Quốc. Th&aacute;ng ba năm ngo&aacute;i, họ b&aacute;c bỏ vai tr&ograve; trung gian của H&agrave;n Quốc, gọi ch&iacute;nh quyền Moon Jae-in l&agrave; một &quot;người chơi chứ kh&ocirc;ng phải l&agrave; người ph&acirc;n xử kh&aacute;ch quan&quot; v&igrave; họ l&agrave; đồng minh của Washington. Sau đ&oacute;, họ r&uacute;t nh&acirc;n vi&ecirc;n khỏi văn ph&ograve;ng li&ecirc;n lạc chung trong một khoảng thời gian. Hồi th&aacute;ng ba, Triều Ti&ecirc;n n&oacute;i rằng văn ph&ograve;ng Tổng thống Moon Jae-in &quot;c&oacute; lối suy nghĩ khờ dại&quot;.</p>\r\n\r\n<p>Đằng sau những lời ch&igrave; chiết của Triều Ti&ecirc;n với H&agrave;n Quốc l&agrave; sự thất vọng của họ với ch&iacute;nh quyền Trump.&nbsp;<strong>Việc ph&aacute; hủy văn ph&ograve;ng li&ecirc;n lạc cũng nhằm gửi th&ocirc;ng điệp đến cho Washington.</strong></p>\r\n\r\n<p>Lee Seong-hyon, nh&agrave; ph&acirc;n t&iacute;ch tại Viện Sejong, tổ chức nghi&ecirc;n cứu ở H&agrave;n Quốc, cho biết nền kinh tế vốn yếu k&eacute;m của Triều Ti&ecirc;n trở n&ecirc;n tồi tệ hơn do Covid-19 l&agrave;m t&ecirc; liệt thương mại với nước ngo&agrave;i.</p>\r\n\r\n<p>&quot;Họ phải b&agrave;y tỏ sự thất vọng v&agrave; bất m&atilde;n, nhưng họ sợ bị đ&aacute;p trả nếu trực tiếp khi&ecirc;u kh&iacute;ch Mỹ&quot;, Lee n&oacute;i. &quot;V&igrave; vậy, họ h&agrave;nh động giống như c&acirc;u n&oacute;i của người Triều Ti&ecirc;n: &#39;Nếu gh&eacute;t người h&agrave;ng x&oacute;m th&igrave; h&atilde;y đ&aacute; con ch&oacute; của anh ta&quot;.</p>\r\n\r\n<p>C&aacute;c nh&agrave; ph&acirc;n t&iacute;ch cho biết những rắc rối trong nước m&agrave; Trump đang đối mặt, gồm hệ quả kinh tế do dịch v&agrave; bất ổn d&acirc;n sự sau c&aacute;i chết của George Floyd, gi&uacute;p Triều Ti&ecirc;n c&oacute; cơ hội v&agrave;ng để khuấy động t&igrave;nh h&igrave;nh.</p>\r\n\r\n<p>Bằng c&aacute;ch l&agrave;m gia tăng căng thẳng tr&ecirc;n b&aacute;n đảo Triều Ti&ecirc;n, một số người cho rằng &ocirc;ng Kim đang g&acirc;y &aacute;p lực để &ocirc;ng Trump cung cấp cho &ocirc;ng &iacute;t nhất l&agrave; một thỏa thuận giảm leo thang căng thẳng, dưới h&igrave;nh thức nới lỏng c&aacute;c biện ph&aacute;p trừng phạt, nếu Tổng thống Mỹ kh&ocirc;ng muốn ch&iacute;nh s&aacute;ch ngoại giao với Triều Ti&ecirc;n ho&agrave;n to&agrave;n tan vỡ trước cuộc bầu cử th&aacute;ng 11.</p>\r\n\r\n<p>Trump đ&atilde; nhiều lần khoe mối quan hệ c&aacute; nh&acirc;n với &ocirc;ng Kim l&agrave; một trong những th&agrave;nh tựu ch&iacute;nh s&aacute;ch đối ngoại lớn nhất của m&igrave;nh, n&oacute;i rằng điều đ&oacute; đ&atilde; gi&uacute;p ngăn chặn chiến tranh với Triều Ti&ecirc;n.</p>\r\n\r\n<p>B&igrave;nh Nhưỡng gần đ&acirc;y kịch liệt chỉ tr&iacute;ch việc người đ&agrave;o tẩu ở H&agrave;n Quốc thả truyền đơn chống ch&iacute;nh quyền sang Triều Ti&ecirc;n. Họ thể hiện n&oacute; như minh chứng cho thấy H&agrave;n Quốc đ&atilde; thất bại trong việc thực thi c&aacute;c thỏa thuận li&ecirc;n Triều. Khi &ocirc;ng Kim v&agrave; &ocirc;ng Moon gặp nhau v&agrave;o năm 2018, họ đ&atilde; hứa sẽ chấm dứt h&agrave;nh vi tuy&ecirc;n truyền xuy&ecirc;n bi&ecirc;n giới như vậy.</p>\r\n\r\n<p>Đầu th&aacute;ng n&agrave;y, Triều Ti&ecirc;n cắt mọi li&ecirc;n lạc với H&agrave;n Quốc v&agrave; cảnh b&aacute;o sẽ c&oacute; những h&agrave;nh động mạnh mẽ&nbsp;hơn. H&ocirc;m 16/6, v&agrave;i giờ trước khi ph&aacute; hủy văn ph&ograve;ng li&ecirc;n lạc, qu&acirc;n đội Triều Ti&ecirc;n dọa sẽ t&aacute;i triển khai qu&acirc;n đến khu vực gần bi&ecirc;n giới H&agrave;n Quốc.</p>\r\n\r\n<p>Theo thỏa thuận được k&yacute; kết từ năm 2000, Triều Ti&ecirc;n đ&atilde; r&uacute;t một số đơn vị bi&ecirc;n ph&ograve;ng để mở đường nối H&agrave;n Quốc với n&uacute;i Kumgang - điểm nghỉ m&aacute;t ở Triều Ti&ecirc;n nằm trong chương tr&igrave;nh th&iacute; điểm du lịch li&ecirc;n Triều - v&agrave; đến Kaesong, nơi hai nước c&ugrave;ng vận h&agrave;nh một khu c&ocirc;ng nghiệp nhiều năm trước khi văn ph&ograve;ng li&ecirc;n lạc mở cửa.</p>\r\n\r\n<p><strong>Lời đe dọa của qu&acirc;n đội Triều Ti&ecirc;n b&aacute;o hiệu rằng B&igrave;nh Nhưỡng c&oacute; thể ph&aacute; hủy c&aacute;c cơ sở do H&agrave;n Quốc x&acirc;y dựng ở Kaesong c&ugrave;ng Kumgang v&agrave; triển khai binh sĩ tới Khu Phi qu&acirc;n sự,</strong>&nbsp;Cheong Seong-chang, chuy&ecirc;n gia tại Viện Sejong, n&oacute;i . Cả hai cơ sở ở Kaesong v&agrave; Kumgang đều kh&ocirc;ng hoạt động nhiều năm, sau khi bị đ&oacute;ng cửa giữa l&uacute;c căng thẳng về vũ kh&iacute; hạt nh&acirc;n của Triều Ti&ecirc;n tăng cao.</p>\r\n\r\n<p>&quot;Triều Ti&ecirc;n đ&atilde; kết luận rằng họ kh&ocirc;ng c&ograve;n c&oacute; thể mong đợi bất cứ điều g&igrave; từ ch&iacute;nh quyền Moon Jae-in&quot;, Cheong n&oacute;i, đề cập đến việc H&agrave;n Quốc từ chối mở lại c&aacute;c dự &aacute;n ở&nbsp;Kaesong v&agrave; Kumgang, vốn l&agrave; nguồn tiền mặt quan trọng cho B&igrave;nh Nhưỡng .</p>\r\n\r\n<p>Khi cuộc bầu cử ở Mỹ ng&agrave;y c&agrave;ng tới gần, Triều Ti&ecirc;n c&oacute; thể chuyển c&aacute;c h&agrave;nh động khi&ecirc;u kh&iacute;ch sang hướng đe dọa Washington, như thử nghiệm t&ecirc;n lửa đạn đạo ph&oacute;ng từ t&agrave;u ngầm hay t&ecirc;n lửa đạn đạo xuy&ecirc;n lục địa, Sin Beom-chul, nh&agrave; ph&acirc;n t&iacute;ch tại Viện Nghi&ecirc;n cứu H&agrave;n Quốc về Chiến lược Quốc gia ở Seoul, n&oacute;i.</p>\r\n\r\n<p>&quot;Gia tăng căng thẳng gia với H&agrave;n Quốc l&agrave; một phần trong chiến lược lớn hơn của Kim Jong-un nhằm g&acirc;y &aacute;p lực với Mỹ&quot;,&nbsp;Sin n&oacute;i.</p>\r\n\r\n<ul>\r\n	<li><a href=\"https://vnexpress.net/em-gai-kim-jong-un-nang-vi-the-4114923.html\">Em g&aacute;i Kim Jong-un n&acirc;ng vị thế</a></li>\r\n	<li><a href=\"https://vnexpress.net/my-trieu-tu-moi-tinh-nong-am-den-ac-mong-dem-den-4115556.html\">Mỹ - Triều từ &#39;mối t&igrave;nh nồng ấm&#39; đến &#39;&aacute;c mộng đ&ecirc;m đen&#39;</a></li>\r\n</ul>\r\n\r\n<p><strong>Phương V</strong>ũ (Theo&nbsp;<em>NYTimes</em>)</p>', 1, 1, 0, 1, 1, 'đời sống', NULL, NULL),
(19, 'vi', 'Em gái Kim Jong-un \'nói là làm\'', 'Kim Yo-jong cuối tuần trước cảnh báo một văn phòng liên lạc chung liên Triều sẽ \"bị sụp đổ hoàn toàn\". Hôm qua, Bình Nhưỡng biến nó thành đống đổ nát.\r\n\r\nKim Yo-jong, em gái lãnh đạo Triều Tiên Kim Jong-un, là cố vấn được ông hết mực tin tưởng, là một trong những phụ nữ quyền lực nhất nước này. Thông tin về Kim Yo-jong trước đây rất hiếm hoi nhưng đang tăng lên nhanh chóng.', 'upload/images/jhh-1592310145-2023-159236875524.jpg', '2020-06-09', 0, '<p>Th&ocirc;ng b&aacute;o đầu ti&ecirc;n của Triều Ti&ecirc;n được đưa ra dưới t&ecirc;n Kim Yo-jong l&agrave; v&agrave;o hồi th&aacute;ng ba nhưng thời gian gần đ&acirc;y, c&ocirc; li&ecirc;n tục ra mặt chỉ tr&iacute;ch H&agrave;n Quốc về việc những người đ&agrave;o tẩu Triều Ti&ecirc;n thường xuy&ecirc;n gửi truyền đơn chống B&igrave;nh Nhưỡng qua bi&ecirc;n giới.</p>\r\n\r\n<p>Ch&iacute;nh thức, Kim Yo-jong chỉ l&agrave; ủy vi&ecirc;n dự khuyết Bộ Ch&iacute;nh trị Trung ương đảng Lao động Triều Ti&ecirc;n. Tuy nhi&ecirc;n, trong tuy&ecirc;n bố đưa ra h&ocirc;m 13/6, được tr&iacute;ch dẫn bởi h&atilde;ng th&ocirc;ng tấn trung ương KCNA, c&ocirc; tuy&ecirc;n bố c&oacute; thẩm quyền &quot;được trao bởi l&atilde;nh tụ tối cao, đảng v&agrave; nh&agrave; nước&quot;.</p>\r\n\r\n<p>Theo th&ocirc;ng tin từ Bộ Thống nhất Triều Ti&ecirc;n, Kim Yo-jong sinh năm 1988, l&agrave; một trong ba người con của cố l&atilde;nh đạo Kim Jong-il v&agrave; người vợ thứ ba. Kim Yo-jong được học tập tại Thụy Sĩ c&ugrave;ng anh trai, sau đ&oacute; nhanh ch&oacute;ng thăng tiến trong bộ m&aacute;y cầm quyền Triều Ti&ecirc;n sau khi Kim Jong-un l&ecirc;n nắm quyền l&atilde;nh đạo đất nước năm 2011.</p>\r\n\r\n<p>Sự tồn tại của Kim Yo-jong gần như kh&ocirc;ng được thế giới biết đến cho tới đ&aacute;m tang của cố l&atilde;nh đạo Kim Jong-il hồi th&aacute;ng 12/2011, khi đứng ph&iacute;a sau anh trai Kim Jong-un với khu&ocirc;n mặt thẫn thờ, đẫm nước mắt. Giờ đ&acirc;y, c&ocirc; xuất hiện với tần suất d&agrave;y đặc hơn b&ecirc;n cạnh anh trai, đảm nhận vai tr&ograve; cố vấn th&acirc;n cận.</p>\r\n\r\n<p>Kim Yo-jong &quot;rất tận tụy trong nỗ lực th&uacute;c đẩy vị thế l&atilde;nh đạo tối cao của anh trai, n&acirc;ng cao h&igrave;nh ảnh trong nước v&agrave; quốc tế của Kim Jong-un. Tr&ecirc;n thực tế, c&ocirc; giống như ch&aacute;nh văn ph&ograve;ng của l&atilde;nh đạo Triều Ti&ecirc;n&quot;, Katharine Moon, gi&aacute;o sư ch&iacute;nh trị tại Đại học Wellesley, Mỹ, nhận định.</p>\r\n\r\n<p>Trong chuyến đi k&eacute;o d&agrave;i 60 giờ bằng t&agrave;u hỏa tới H&agrave; Nội dự hội nghị thượng đỉnh lần hai với Tổng thống Mỹ Donald Trump, Kim Yo-jong được nh&igrave;n thấy mang gạt t&agrave;n đến cho Kim Jong-un khi anh trai đang h&uacute;t thuốc l&aacute;.</p>\r\n\r\n<p>&quot;Kh&ocirc;ng c&ograve;n nghi ngờ g&igrave;&quot; về việc Kim Jong-un c&oacute; mối quan hệ đặc biệt th&acirc;n thiết với em g&aacute;i, Yang Moo-jin từ Đại học Nghi&ecirc;n cứu Triều Ti&ecirc;n ở Seoul, H&agrave;n Quốc, nhận x&eacute;t. &quot;Jong-un v&agrave; Yo-jong đ&atilde; d&agrave;nh phần lớn thời gian ni&ecirc;n thiếu c&ocirc; đơn ở nước ngo&agrave;i c&ugrave;ng nhau. T&ocirc;i nghĩ qu&atilde;ng thời gian n&agrave;y đ&atilde; gi&uacute;p x&acirc;y dựng n&ecirc;n một thứ t&igrave;nh cảm như t&igrave;nh đồng ch&iacute;, b&ecirc;n cạnh t&igrave;nh anh em ruột thịt&quot;.</p>\r\n\r\n<p>Triều Ti&ecirc;n chưa từng c&oacute; l&atilde;nh đạo l&agrave; nữ giới song những đồn đo&aacute;n về tương lai ch&iacute;nh trị v&agrave; khả năng c&ocirc; sẽ l&agrave; người kế nhiệm anh trai m&igrave;nh bắt đầu xuất hiện hồi th&aacute;ng 4, sau khi l&atilde;nh đạo Triều Ti&ecirc;n biến mất suốt nhiều ng&agrave;y.</p>\r\n\r\n<p>Quyền lực l&atilde;nh đạo ở Triều Ti&ecirc;n l&acirc;u nay vẫn lu&ocirc;n l&agrave; vấn đề nội bộ gia đ&igrave;nh họ Kim. Kim Yo-jong giờ đ&acirc;y nổi l&ecirc;n như một th&agrave;nh vi&ecirc;n xuất ch&uacute;ng của &quot;d&ograve;ng m&aacute;u Paektu&quot;, kh&aacute;i niệm ở Triều Ti&ecirc;n để mi&ecirc;u tả cố l&atilde;nh đạo, nh&agrave; khai quốc Kim Nhật Th&agrave;nh, c&ugrave;ng c&aacute;c con ch&aacute;u.</p>\r\n\r\n<p><img alt=\"Lãnh đạo Triều Tiên Kim Jong-un và em gái Kim Yo-jong trong cuộc gặp Tổng thống Hàn Quốc Moon Jae-in ở Bàn Môn Điếm hồi tháng 4/2018. Ảnh: Reuters.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/17/yo-jong-5525-1592368756.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=bADZ2WP921g0DuH4BI12rQ\" /></p>\r\n\r\n<p>L&atilde;nh đạo Triều Ti&ecirc;n Kim Jong-un v&agrave; em g&aacute;i Kim Yo-jong trong cuộc gặp Tổng thống H&agrave;n Quốc Moon Jae-in ở B&agrave;n M&ocirc;n Điếm hồi th&aacute;ng 4/2018. Ảnh:&nbsp;<em>Reuters.</em></p>\r\n\r\n<p>Theo giới ph&acirc;n t&iacute;ch, việc truyền th&ocirc;ng nh&agrave; nước Triều Ti&ecirc;n l&agrave;m bật h&igrave;nh ảnh v&agrave; vai tr&ograve; của Kim Yo-jong để phản ứng trước&nbsp;h&agrave;nh động rải truyền đơn từ ph&iacute;a H&agrave;n Quốc dường như nhằm gi&uacute;p c&ocirc; n&acirc;ng cao uy t&iacute;n trong giới qu&acirc;n sự nước n&agrave;y. Việc Triều Ti&ecirc;n ph&aacute; hủy văn ph&ograve;ng li&ecirc;n lạc hai miền kh&ocirc;ng l&acirc;u sau tuy&ecirc;n bố từ Kim Yo-jong c&agrave;ng cho thấy quyền lực mạnh mẽ m&agrave; c&ocirc; nắm trong tay.</p>\r\n\r\n<p>Trước đ&acirc;y, c&aacute;c hoạt động của Kim Yo-jong thường gắn liền với những nỗ lực ngoại giao Triều Ti&ecirc;n. Hồi năm 2018, h&igrave;nh ảnh Kim Yo-jong được lan truyền rộng r&atilde;i tr&ecirc;n truyền th&ocirc;ng quốc tế khi c&ocirc;, với tư c&aacute;ch ph&aacute;i vi&ecirc;n của anh trai Kim Jong-un, c&ugrave;ng đo&agrave;n thể thao Triều Ti&ecirc;n tham dự Olympic m&ugrave;a Đ&ocirc;ng ở Incheon, H&agrave;n Quốc. C&ocirc; trở th&agrave;nh th&agrave;nh vi&ecirc;n đầu ti&ecirc;n trong gia tộc họ Kim đặt ch&acirc;n l&ecirc;n H&agrave;n Quốc kể từ cuộc Chiến tranh Triều Ti&ecirc;n (1950 - 1953).</p>\r\n\r\n<p><img alt=\"Hiện trường Triều Tiên giật sập văn phòng liên lạc chung với Hàn Quốc. Ảnh: KCNA.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/17/van-phong-lien-lac-1-6273-1592361540.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=zY8Fa4ejqfzCIkjBTu9KaQ\" /></p>\r\n\r\n<p>Triều Ti&ecirc;n giật sập văn ph&ograve;ng li&ecirc;n lạc chung với H&agrave;n Quốc ở th&agrave;nh phố Kaesong, bi&ecirc;n giới Triều Ti&ecirc;n ng&agrave;y 16/6. Ảnh:&nbsp;<em>KCNA</em>.</p>\r\n\r\n<p>Thời điểm đ&oacute;, khi Kim Yo-jong c&oacute; cuộc gặp ngắn với c&aacute;c quan chức Seoul, trưởng ph&aacute;i đo&agrave;n Triều Ti&ecirc;n Kim Yong-nam, l&uacute;c bấy giờ l&agrave; chủ tịch quốc hội, đ&atilde; đề nghị nhường c&ocirc; vị tr&iacute; ngồi danh dự.</p>\r\n\r\n<p>Th&agrave;nh c&ocirc;ng của Olympic m&ugrave;a Đ&ocirc;ng năm 2018 đ&atilde; mở ra những cuộc thảo luận tiến triển nhanh ch&oacute;ng tr&ecirc;n b&aacute;n đảo Triều Ti&ecirc;n. Kim Yo-jong đ&atilde; c&ugrave;ng anh trai tham dự h&agrave;ng loạt hội nghị thượng đỉnh với vai tr&ograve; trợ l&yacute;. Thỉnh thoảng, c&ocirc; đứng chung khung h&igrave;nh với l&atilde;nh đạo Triều Ti&ecirc;n khi &ocirc;ng s&aacute;nh vai b&ecirc;n cạnh Tổng thống Mỹ Donald Trump hay Tổng thống H&agrave;n Quốc Moon Jae-in.</p>\r\n\r\n<ul>\r\n	<li><a href=\"https://vnexpress.net/my-trieu-tu-moi-tinh-nong-am-den-ac-mong-dem-den-4115556.html\">Mỹ - Triều từ &#39;mối t&igrave;nh nồng ấm&#39; đến &#39;&aacute;c mộng đ&ecirc;m đen&#39;</a></li>\r\n	<li><a href=\"https://vnexpress.net/khoanh-khac-van-phong-lien-lac-han-trieu-bi-giat-sap-4116788.html\">Khoảnh khắc Văn ph&ograve;ng Li&ecirc;n lạc H&agrave;n - Triều bị giật sập</a></li>\r\n	<li><a href=\"https://vnexpress.net/han-quoc-the-cung-ran-neu-trieu-tien-tiep-tuc-gay-han-4116617.html\">H&agrave;n Quốc thề cứng rắn nếu Triều Ti&ecirc;n tiếp tục g&acirc;y hấn</a></li>\r\n</ul>\r\n\r\n<p><strong>Vũ Ho&agrave;ng</strong>&nbsp;(Theo&nbsp;<em>AFP</em>)</p>', 1, 1, 0, 1, 1, 'đời sống', NULL, NULL),
(20, 'vi', 'Cuộc đua khó lường vào Nhà Trắng', 'Dù Trump đang tụt sau Biden trong các cuộc thăm dò, kết quả bầu cử được cho là vẫn khó lường, nếu nhìn từ thể hiện của Trump năm 2016.  \r\n\r\nKhi còn 140 ngày là đến bầu cử tổng thống Mỹ, các kết quả thăm dò có vẻ ảm đạm đối với Tổng thống Donald Trump. Nhiều cử tri dường như mệt mỏi trước những phát ngôn và dòng tweet bị cho là gây chia rẽ của ông.', 'upload/images/800-jpeg-6702-1592203256483.jpg', '2020-06-18', 0, '<p>Một loạt h&agrave;nh động bị đ&aacute;nh gi&aacute; sai lầm của Trump sau c&aacute;i chết của George Floyd, như điều cảnh s&aacute;t phun hơi cay dẹp người biểu t&igrave;nh để mở đường cho &ocirc;ng đến nh&agrave; thờ gần Nh&agrave; Trắng, hay việc khẳng định c&oacute; thể khắc phục nạn ph&acirc;n biệt chủng tộc nhanh ch&oacute;ng v&agrave; dễ d&agrave;ng, khiến ngay cả một số th&agrave;nh vi&ecirc;n đảng Cộng h&ograve;a cũng ngao ng&aacute;n.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, c&aacute;c chiến lược gia từ cả phe D&acirc;n chủ v&agrave; Cộng h&ograve;a đều gần như nhất tr&iacute; rằng kh&ocirc;ng ai c&oacute; thể n&oacute;i trước một vấn đề như bất b&igrave;nh đẳng chủng tộc sẽ ra&nbsp;sao v&agrave;o th&aacute;ng 11, đặc biệt khi cuộc sống v&agrave; sinh kế của người Mỹ đang bị đe dọa v&igrave; Covid-19, cũng như nguy cơ nền kinh tế sụp đổ.</p>\r\n\r\n<p><img alt=\"Tổng thống Trump (phải) tại Nhà Trắng hôm 5/4 và Biden, ứng viên tổng thống đảng Dân chủ, tại thành phố Wilmington, bang Delaware hôm 12/3. Ảnh: AP.\" src=\"https://i1-vnexpress.vnecdn.net/2020/06/15/800-jpeg-6702-1592203256.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=a0xxnUqd8GeecaQLw5KZxw\" /></p>\r\n\r\n<p>Tổng thống Trump (phải) tại Nh&agrave; Trắng h&ocirc;m 5/4 v&agrave; Biden, ứng vi&ecirc;n tổng thống đảng D&acirc;n chủ, tại th&agrave;nh phố&nbsp;Wilmington, bang Delaware h&ocirc;m 12/3. Ảnh:&nbsp;<em>AP</em>.</p>\r\n\r\n<p>Họ cũng lưu &yacute;&nbsp;<strong>đảng D&acirc;n chủ c&oacute; thể gặp rắc rối nếu qu&aacute; tự tin v&agrave; cho rằng những ph&aacute;t ng&ocirc;n k&iacute;ch động của Trump sẽ khiến &ocirc;ng phải trả gi&aacute; trong cuộc bầu cử</strong>, sai lầm m&agrave; một số th&agrave;nh vi&ecirc;n đảng D&acirc;n chủ từng mắc phải hồi năm 2016. B&ecirc;n cạnh đ&oacute;, phe D&acirc;n chủ c&ograve;n đang đối mặt với &aacute;p lực ng&agrave;y c&agrave;ng tăng từ c&aacute;c nh&agrave; hoạt động c&aacute;nh tả k&ecirc;u gọi cắt ng&acirc;n s&aacute;ch cảnh s&aacute;t.</p>\r\n\r\n<p>&quot;Đ&acirc;y l&agrave; tin tốt cho Trump. Phe Cộng h&ograve;a sẽ nhẹ tay hơn nhiều so với D&acirc;n chủ trong vấn đề cải tổ lực lượng cảnh s&aacute;t&quot;, thượng nghị sĩ Lindsey Graham, Chủ tịch Ủy ban Tư ph&aacute;p Thượng viện, nhận x&eacute;t. &quot;T&ocirc;i nghĩ hầu hết mọi người, đặc biệt l&agrave; phụ nữ c&oacute; con, muốn nhấc điện thoại v&agrave; gọi cho cảnh s&aacute;t chứ kh&ocirc;ng phải một nh&acirc;n vi&ecirc;n x&atilde; hội&quot;.</p>\r\n\r\n<p>Trump từng cố gắng truyền đi th&ocirc;ng điệp tương tự về an to&agrave;n cộng đồng trước thềm bầu cử giữa nhiệm kỳ hồi năm 2018. Khi đ&oacute;, Tổng thống Mỹ l&agrave;m dấy l&ecirc;n mối lo ngại về những đo&agrave;n người di cư m&agrave; &ocirc;ng n&oacute;i rằng đang vượt bi&ecirc;n v&agrave; đe dọa người Mỹ, đồng thời cam kết thực thi &quot;luật ph&aacute;p v&agrave; trật tự&quot;.</p>\r\n\r\n<p>Hiện chưa r&otilde; c&aacute;ch tiếp cận theo luật ph&aacute;p v&agrave; trật tự của Trump c&oacute; gi&uacute;p &iacute;ch cho &ocirc;ng v&agrave;o th&aacute;ng 11 hay kh&ocirc;ng, do c&aacute;c con số thăm d&ograve; c&ograve;n nhiều m&acirc;u thuẫn. Cuộc khảo s&aacute;t tuần trước của Trung t&acirc;m Pew cho thấy 2/3 người Mỹ trưởng th&agrave;nh ủng hộ phong tr&agrave;o &quot;Mạng sống người da m&agrave;u quan trọng&quot;. 6/10 số người tham gia cũng tin rằng Trump phản ứng sai lầm với c&aacute;c cuộc biểu t&igrave;nh.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, cuộc thăm d&ograve; mới đ&acirc;y của ABC News-Ipsos cho thấy 64% người Mỹ phản đối nỗ lực cắt giảm ng&acirc;n s&aacute;ch của cảnh s&aacute;t. 60% kh&ocirc;ng ủng hộ việc cắt ng&acirc;n s&aacute;ch của cảnh s&aacute;t để ph&acirc;n bổ tiền sang c&aacute;c chương tr&igrave;nh chăm s&oacute;c sức khỏe t&acirc;m l&yacute;, nh&agrave; ở v&agrave; gi&aacute;o dục.</p>\r\n\r\n<p>Biden tuần trước cho biết &ocirc;ng kh&ocirc;ng ủng hộ lời k&ecirc;u gọi cắt giảm ng&acirc;n s&aacute;ch cảnh s&aacute;t, nhưng phe Cộng h&ograve;a vẫn nh&acirc;n cơ hội n&agrave;y để chỉ tr&iacute;ch cựu ph&oacute; tổng thống Mỹ, một phần bởi &ocirc;ng từng b&agrave;y tỏ ủng hộ &quot;điều chỉnh viện trợ li&ecirc;n bang cho cảnh s&aacute;t&quot;, dựa tr&ecirc;n việc xem x&eacute;t liệu họ c&oacute; &quot;đ&aacute;p ứng những ti&ecirc;u chuẩn cơ bản nhất định về c&aacute;ch cư xử th&iacute;ch hợp v&agrave; sự ch&iacute;nh trực&quot; hay kh&ocirc;ng.</p>\r\n\r\n<p>Hạ nghị sĩ D&acirc;n chủ Jim Clyburn cảnh b&aacute;o lời k&ecirc;u gọi cắt ng&acirc;n s&aacute;ch cảnh s&aacute;t đang g&acirc;y ra hiểu lầm trong cuộc tranh luận. &quot;Kh&ocirc;ng ai c&oacute; &yacute; định đ&oacute;. Ch&uacute;ng t&ocirc;i c&oacute; thể t&aacute;i cấu tr&uacute;c lực lượng cảnh s&aacute;t, h&igrave;nh dung lại ch&iacute;nh s&aacute;ch. Sự thật l&agrave; cảnh s&aacute;t đ&oacute;ng vai tr&ograve; quan trọng&quot;, &ocirc;ng n&oacute;i.</p>\r\n\r\n<p>Theo b&igrave;nh luận vi&ecirc;n Maeve Reston của&nbsp;<em>CNN</em>,&nbsp;<strong>th&aacute;ch thức của Biden trong những th&aacute;ng tới l&agrave; t&igrave;m điểm chung giữa c&aacute;c b&ecirc;n trong đảng D&acirc;n chủ</strong>, khi họ đang cho thấy chia rẽ s&acirc;u sắc về c&aacute;ch khắc phục nạn ph&acirc;n biệt chủng tộc. Một trong c&aacute;c thế mạnh chủ chốt của Trump l&agrave; những người ủng hộ &ocirc;ng rất nhiệt t&igrave;nh với chiến dịch t&aacute;i tranh cử, c&oacute; thể gi&uacute;p Tổng thống tạo ra sự đồng thuận trong vấn đề.</p>\r\n\r\n<p>H&ocirc;m 14/6, Tim Scott v&agrave; James Lankford, hai thượng nghị sĩ phụ tr&aacute;ch kế hoạch cải tổ cảnh s&aacute;t của đảng Cộng h&ograve;a, &aacute;m chỉ rằng họ đang xem x&eacute;t những &yacute; tưởng c&oacute; thể thu h&uacute;t được sự đồng thuận lưỡng đảng. Lankford cho biết &ocirc;ng c&ugrave;ng nh&oacute;m ph&aacute;p l&yacute; đang thảo luận với cả c&aacute;c chỉ huy cảnh s&aacute;t v&agrave; nh&agrave; hoạt động, nhằm thu thập &quot;những &yacute; kiến tốt nhất&quot; v&agrave; t&igrave;m ra c&aacute;ch giải quyết vấn đề thực sự.</p>\r\n\r\n<p>&quot;Đảng D&acirc;n chủ đ&atilde; đưa ra một đề xuất. Tổng thống cũng đang thực hiện điều n&agrave;y. Ch&uacute;ng t&ocirc;i sẽ c&ocirc;ng bố đề xuất từ ph&iacute;a đảng Cộng h&ograve;a. Tất cả sẽ được kết hợp. Ch&uacute;ng t&ocirc;i sẽ cố gắng ban h&agrave;nh những điều luật th&iacute;ch hợp&quot;, Lankford n&oacute;i.</p>\r\n\r\n<p>Trong khi đ&oacute;, Scott cho biết Trump &quot;đang tham gia một c&aacute;ch hữu &iacute;ch v&agrave; c&oacute; t&iacute;nh x&acirc;y dựng&quot; v&agrave;o qu&aacute; tr&igrave;nh soạn thảo luật. Sắc lệnh h&agrave;nh ph&aacute;p m&agrave; Tổng thống dự kiến ban h&agrave;nh trong tuần n&agrave;y &quot;thực sự dựa tr&ecirc;n cơ sở dữ liệu quốc gia về những h&agrave;nh vi sai phạm của cảnh s&aacute;t&quot;.</p>\r\n\r\n<p>Theo b&igrave;nh luận vi&ecirc;n Reston, c&aacute;c đồng minh của Trump đang tăng cường quảng b&aacute; cho vai tr&ograve; của Tổng thống trong việc đưa ra những cải c&aacute;ch c&oacute; thể gi&uacute;p kiềm chế cảnh s&aacute;t. Trong cuộc phỏng vấn với Fox News tuần trước, Trump cũng b&agrave;y tỏ bất b&igrave;nh với cảnh s&aacute;t khống chế bằng c&aacute;ch gh&igrave; cổ, quan điểm c&oacute; thể được lưỡng đảng trong quốc hội đồng thuận.</p>\r\n\r\n<p>Mặc d&ugrave; nhiều người Mỹ bị sốc trước hoạt động đẩy l&ugrave;i đ&aacute;m đ&ocirc;ng biểu t&igrave;nh &ocirc;n h&ograve;a gần Nh&agrave; Trắng, c&aacute;c đồng minh của Trump tin&nbsp;<strong>th&ocirc;ng điệp về luật ph&aacute;p v&agrave; trật tự của Trump vẫn c&oacute; thể chiếm được cảm t&igrave;nh của người d&acirc;n</strong>. Nhiều cố vấn cũng khuyến kh&iacute;ch &ocirc;ng điều chỉnh giọng điệu để ph&ugrave; hợp với ho&agrave;n cảnh.</p>\r\n\r\n<p>Trump h&ocirc;m 12/6 dường như đ&atilde; l&agrave;m theo lời khuy&ecirc;n. &Ocirc;ng&nbsp;tuy&ecirc;n bố sẽ thay đổi lịch tr&igrave;nh một buổi vận động tranh cử dự kiến diễn ra tại th&agrave;nh phố Tulsa, bang Oklahoma v&agrave;o 19/6, ng&agrave;y kỷ niệm chấm dứt chế độ n&ocirc; lệ ở Mỹ.</p>\r\n\r\n<p>Quyết định tổ chức buổi vận động ở Tulsa được cho l&agrave; vấn đề lớn, bởi năm 1921 nơi đ&acirc;y từng xảy ra vụ thảm s&aacute;t h&agrave;ng trăm người Mỹ gốc Phi, một trong những sự cố bạo lực chủng tộc tồi tệ nhất lịch sử Mỹ. Trump cho biết &ocirc;ng ho&atilde;n sự kiện để thể hiện sự t&ocirc;n trọng với ng&agrave;y kỷ niệm, dấu hiệu&nbsp;&ocirc;ng đang lắng nghe c&aacute;c cố vấn.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, chỉ v&agrave;i giờ sau, Trump dường như lại &quot;đổ dầu&quot; v&agrave;o cuộc chiến, khi ngụ &yacute; tr&ecirc;n Twitter rằng &ocirc;ng sẽ kh&ocirc;ng xem Giải B&oacute;ng bầu dục Quốc gia nữa, nếu ban tổ chức tiếp tục cho ph&eacute;p c&aacute;c cầu thủ quỳ gối trong l&uacute;c quốc ca được ph&aacute;t, h&agrave;nh động thể hiện sự ủng hộ phong tr&agrave;o biểu t&igrave;nh.</p>\r\n\r\n<p>B&igrave;nh luận vi&ecirc;n Reston cho rằng kh&ocirc;ng ai c&oacute; thể biết trước động th&aacute;i tiếp theo của Trump.</p>\r\n\r\n<ul>\r\n	<li><a href=\"https://vnexpress.net/cat-ngan-sach-canh-sat-cuoc-chien-moi-giua-trump-va-biden-4112807.html\">Cắt ng&acirc;n s&aacute;ch cảnh s&aacute;t - cuộc chiến mới giữa Trump v&agrave; Biden</a></li>\r\n	<li><a href=\"https://vnexpress.net/joe-biden-doi-thu-nhu-nuoc-voi-lua-cua-trump-4111452.html\">Joe Biden - đối thủ &#39;như nước với lửa&#39; của Trump</a><a href=\"https://vnexpress.net/joe-biden-doi-thu-nhu-nuoc-voi-lua-cua-trump-4111452.html#box_comment_vne\">&nbsp;54</a></li>\r\n	<li><a href=\"https://vnexpress.net/hoai-nghi-nuoc-my-truoc-tien-hay-trump-truoc-tien-4111940.html\">Ho&agrave;i nghi &#39;nước Mỹ trước ti&ecirc;n&#39; hay &#39;Trump trước ti&ecirc;n&#39;</a><a href=\"https://vnexpress.net/hoai-nghi-nuoc-my-truoc-tien-hay-trump-truoc-tien-4111940.html#box_comment_vne\">&nbsp;164</a></li>\r\n	<li><a href=\"https://vnexpress.net/trump-tham-vong-tro-thanh-lanh-dao-toan-quyen-4110669.html\">Trump tham vọng trở th&agrave;nh l&atilde;nh đạo to&agrave;n quyền</a></li>\r\n	<li><a href=\"https://vnexpress.net/trump-don-thuong-doc-ma-giua-tam-bao-4109491.html\">Trump &#39;đơn thương độc m&atilde;&#39; giữa t&acirc;m b&atilde;o</a><a href=\"https://vnexpress.net/trump-don-thuong-doc-ma-giua-tam-bao-4109491.html#box_comment_vne\">&nbsp;93</a></li>\r\n</ul>\r\n\r\n<p><strong>&Aacute;nh Ngọc</strong>&nbsp;(Theo&nbsp;<em>CNN</em>)</p>', 1, 1, 0, 1, 1, 'đời sống', NULL, NULL);

